textarea:not([type="search"]):focus { /* èšç„¦æ—¶æ ·å¼ */
    border-color: var(--theme-color-1) !important;
    box-shadow: 0 0 5px color-mix(in srgb, var(--theme-color-1) 30%, transparent) !important;
    background-color: var(--ui-color-main) !important;
}
/* æœç´¢æ¡†ç‰¹æ®Šæ ·å¼ */
textarea[type="search"] {
    background-color: transparent !important;
    border: none !important;
    border-bottom: 1px solid var(--theme-color-2) !important;
    border-radius: 0 !important;
}
/* èŒƒå›´é€‰æ‹©å™¨ç›¸å…³ */
.range-block {
    margin: calc(var(--mainFontSize) * 0.8) 0;
}
.range-block-counter {
