}

/* --- æŒ‰é’®æ ·å¼ --- */
/* ä¸»è¦æŒ‰é’®ï¼šå›¾ç‰‡èƒŒæ™¯åŠå½¢çŠ¶ */
.menu_button:not(.mes_edit_buttons *):not(.secondary):not(.redWarningBG):not(.popup-button-ok),
button:not(.mes_edit_buttons *):not(.secondary):not(.redWarningBG):not(.popup-button-ok) {
    background-image: var(--thin-sticky-note-button-image); /* æŒ‰é’®èƒŒæ™¯å›¾ç‰‡ */
    background-size: 100% 100%;
    background-color: transparent;
    border: none;
    color: var(--text-color-main);
    padding: 8px 18px; 
    border-radius: 0px; /* clip-path å®šä¹‰è¾¹è§’ */
    font-weight: 500;
    text-shadow: none;
    filter: drop-shadow(0 1px 2px color-mix(in srgb, var(--text-color-main) 30%, transparent)); /* å½¢çŠ¶é˜´å½± */
    transition: filter 0.2s ease, transform 0.2s ease;
    clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%); /* TODO: è¯·ç”¨ä½ å®é™…çš„å¤šè¾¹å½¢æ›¿æ¢æ­¤è¡Œ */
    overflow: hidden;
}

/* ä¸»è¦æŒ‰é’®æ‚¬åœ */
.menu_button:not(.mes_edit_buttons *):not(.secondary):not(.redWarningBG):not(.popup-button-ok):hover,
button:not(.mes_edit_buttons *):not(.secondary):not(.redWarningBG):not(.popup-button-ok):hover {
    filter: brightness(0.95) drop-shadow(0 2px 4px color-mix(in srgb, var(--text-color-main) 35%, transparent));
    transform: translateY(-1px); /* ä¸Šæµ®æ•ˆæœ */
}

/* æ¬¡è¦æŒ‰é’®æ ·å¼ */
.menu_button.secondary,
