@keyframes mes_reasoning_smooth_expand { /* å¹³æ»‘å±•å¼€ */
    0% {
        opacity: 0;
        transform: translateY(-8px) scale(0.98);
    }
    40% { opacity: 0.8; }
    100% {
        opacity: 1;
        transform: translateY(0) scale(1);
    }
}

/* å†…å®¹åŒºåˆ—è¡¨/æ®µè½é—´è· */
