/* æ€è€ƒä¸­çŠ¶æ€æµå…‰æ•ˆæœ */
details.mes_reasoning_details[data-state="thinking"] .mes_reasoning_summary::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 200%;
    height: 100%;
    background: linear-gradient(
        90deg,
        transparent 0%,
        color-mix(in srgb, var(--theme-color-1) 5%, transparent) 15%,
        color-mix(in srgb, var(--theme-color-1) 10%, transparent) 25%,
        color-mix(in srgb, var(--theme-color-1) 15%, transparent) 35%,
        color-mix(in srgb, var(--theme-color-1) 20%, transparent) 45%,
        color-mix(in srgb, var(--theme-color-1) 15%, transparent) 55%,
        color-mix(in srgb, var(--theme-color-1) 10%, transparent) 65%,
        color-mix(in srgb, var(--theme-color-1) 5%, transparent) 75%,
        transparent 85%
    );
    animation: mes_reasoning_shimmer 3s cubic-bezier(0.4, 0.0, 0.2, 1) infinite;
    transform: translateX(-100%);
    pointer-events: none;
    z-index: 1;
}
/* å±•å¼€æ—¶åœæ­¢æµå…‰ */
details.mes_reasoning_details[open][data-state="thinking"] .mes_reasoning_summary::after {
    animation: none;
    opacity: 0;
}

/* æ€è€ƒå®ŒæˆåŠ¨ç”»æ•ˆæœ */
details.mes_reasoning_details[data-state="done"] .mes_reasoning_summary::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 150%;
    height: 100%;
    background: linear-gradient(
        90deg,
        transparent 0%,
        color-mix(in srgb, var(--theme-color-accent) 5%, transparent) 20%, /* å®ŒæˆåŠ¨ç”»é¢œè‰² */
        color-mix(in srgb, var(--theme-color-accent) 10%, transparent) 30%,
        color-mix(in srgb, var(--theme-color-accent) 15%, transparent) 40%,
        color-mix(in srgb, var(--theme-color-accent) 20%, transparent) 50%,
        color-mix(in srgb, var(--theme-color-accent) 15%, transparent) 60%,
        color-mix(in srgb, var(--theme-color-accent) 10%, transparent) 70%,
        color-mix(in srgb, var(--theme-color-accent) 5%, transparent) 80%,
        transparent 100%
    );
    animation: mes_reasoning_material_completion 1.4s cubic-bezier(0.3, 0.0, 0.3, 1) forwards;
    pointer-events: none;
    z-index: 1;
}
@keyframes mes_reasoning_material_completion { /* å®ŒæˆåŠ¨ç”» */
    0% {
        transform: translateX(-100%);
        opacity: 0;
    }
    10% { opacity: 0.4; }
    30% { opacity: 0.8; }
    70% { opacity: 0.8; }
    95% { opacity: 0.4; }
    100% {
        transform: translateX(100%);
        opacity: 0;
    }
}
@keyframes mes_reasoning_shimmer { /* æµå…‰åŠ¨ç”» */
    0% {
        transform: translateX(-100%);
        opacity: 0.8;
    }
    50% { opacity: 1; }
    100% {
        transform: translateX(50%);
        opacity: 0.8;
    }
}

