custom_css:
/*
 * SillyTavern Custom UI Theme：蝶々結びbyleviathan
 * 感谢：络络老师的思维链美化
 * 图源pin站及小红书，侵删，二改请标明出处，禁止二传
 */


/* ===== 全局字体（存在字体冲突可删除下列代码） ===== */
@import url("https://fontsapi.zeoseven.com/3/main/result.css");

body, button, input, textarea, code {
    font-family: "undefined"!important;
    font-weight: normal;
}


/* ===== 消息界面美化BEGIN ===== */
.mes:not([is_user="true"]) {
    flex-direction: row-reverse;
}

.mes:not([is_user="true"]) .ch_name {
    text-align: left;
}

.mes[is_user="true"] .ch_name {
    text-align: left;
}


.mes_block {
    position: relative; 
    overflow: visible;  
    margin-top: 5px;   
    padding-top: 315px;  
}


.mes_block::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 390px; 
    top: -35px;   
    left: 0;
    background-image: url('https://files.catbox.moe/9b7j4u.png');
    background-size: 100% 100%; 
    background-repeat: no-repeat;
    z-index: 0; 
}


.mes .name_text {
    position: absolute;
    top: 150px;     
    z-index: 1;  
    color: #6a4b5a; 
    font-family: "Times New Roman", Georgia, "Songti SC", "SimSun", serif;
    font-size: 14px;
    font-weight: bold;
}


.mes[is_user="true"] .name_text{
    left: 200px;
}

.mes:not([is_user="true"])  .name_text{
    right: 200px;
}

.mes .timestamp {
    position: absolute;
    top: 180px;     
    z-index: 1;   
    font-family: "Times New Roman", Georgia, "Songti SC", "SimSun", serif;
    font-size: 12px;
    color: #6a4b5a; 
}


.mes[is_user="true"] .timestamp{
    left: 170px;
}

.mes:not([is_user="true"])  .timestamp{
    right: 170px;
}

.mes_botton{
    position: absolute;
}



.mes .avatar img {
    width: 100%;
    height: 100%;
    object-fit: cover; 
}

/* --- 用户消息 --- */
.mes[is_user="true"] .mes_block::before {
    right: 0;
    background-position: right top;
    transform: scaleX(-1); 
}

.mes[is_user="true"] .avatar {
    right: 120px; 
    transform: rotate(25deg);
}

/* --- AI消息 --- */
.mes:not([is_user="true"]) .mes_block::before {
    left: 0;
    background-position: left top;
}

.mes:not([is_user="true"]) .avatar {
    left: 120px; 
    transform: rotate(-25deg);
}


.mes .mesIDDisplay {
    position: absolute !important;
    z-index: 3;
    top: 18rem;      
    transform: rotate(10deg);
    font-size: 10px;
}

.mes .tokenCounterDisplay {
    position: absolute !important;
    z-index: 3;
    top: 19.3rem;    
    transform: rotate(10deg);
    font-size: 10px;
}

.mes .mes_timer {
    position: absolute !important;
    z-index: 3;
    top: 18.8rem;     
    transform: rotate(10deg);
    font-size: 10px;
}

.mes:not([is_user="true"]) .mesIDDisplay{     
    right: 5.125rem;
}

.mes:not([is_user="true"]) .tokenCounterDisplay {   
    right: 3.25rem;
}

.mes:not([is_user="true"]) .mes_timer{     
    right: 6rem;
}

.mes[is_user="true"] .mesIDDisplay,
.mes[is_user="true"] .tokenCounterDisplay,
.mes[is_user="true"] .mes_timer {
    right: auto;
    left: 4.85rem;
    transform: rotate(-10deg);
}

.swipes-counter {
    z-index: 3;
}

/* --- 头像样式与定位 --- */
.mes .avatar {
    position: absolute;
    top: 80px; 
    width: 140px; 
    height: 140px; 
    border-radius: 12px; 
    z-index: 1; 
    overflow: visible;  
    border: none; 
    box-shadow: none;
}

.mes .avatar img {
    width: 100%;
    height: 100%;
    object-fit: cover; 
    border-radius: 12px!important;  
}

/* --- 头像框伪元素 --- */
.mes .avatar::after {
    content: '';
    position: absolute;
    width: 170px;  
    height: 200px; 
    top: -45px;    
    left: -15px;   

    /*GIF背景图 */
    background-image: url('https://files.catbox.moe/cbuh0u.gif');
    background-size: 100% 100%; 
    background-repeat: no-repeat;

    pointer-events: none;
}

.avatar img {
    border: none;
    box-shadow: none;
}


/*美化顶部导航栏 #top-bar */

#top-bar {

    background-image: url('https://files.catbox.moe/5n1r9n.png');
    background-size: 100% 100%; 
    background-position: center;
    background-repeat: no-repeat;


    background-color: transparent !important;
    border: none !important;
    box-shadow: none !important;

    height: 2rem; 
    padding: 0; 

    display: flex;
    align-items: center;
}

#top-bar .drawer-toggle {
    height: 100%; 
    display: flex;
    align-items: center;
}

#top-bar .drawer-icon {
    color: white; 
    font-size: 1.25rem; 
    text-shadow: 0 0.125rem 0.25rem rgba(40, 70, 100, 0.5);
    position: relative;
    top: -2px; 
}

#logo_block {
    background: none !important;
}

body.no-blur #top-bar{
    background-color: transparent!important;
}


/* --- 自定义图标替换 --- */
.drawer-icon {
    font-size: 0 !important;
    color: transparent !important;
    line-height: calc(var(--mainFontSize) * 2.1);
    width: calc(var(--mainFontSize) * 2.1);
    height: calc(var(--mainFontSize) * 2.1);
    display: inline-block;
    vertical-align: middle;
    position: relative;
}

.drawer-icon::before {
    content: "";
    display: flex;
    width: 120%;
    height: 120%;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}

/* --- 以下图标 URL 分配 --- */
#ai-config-button .drawer-icon::before {
    background-image: url('https://files.catbox.moe/d6ou3n.png');
}
#sys-settings-button .drawer-icon::before {
    background-image: url('https://files.catbox.moe/d6ou3n.png');
}
#advanced-formatting-button .drawer-icon::before {
    background-image: url('https://files.catbox.moe/d6ou3n.png');
}
#WI-SP-button .drawer-icon::before {
    background-image: url('https://files.catbox.moe/d6ou3n.png');
}
#user-settings-button .drawer-icon::before {
    background-image: url('https://files.catbox.moe/d6ou3n.png');
}
#logo_block .drawer-icon::before {
    background-image: url('https://files.catbox.moe/d6ou3n.png');
}
#extensions-settings-button .drawer-icon::before {
    background-image: url('https://files.catbox.moe/d6ou3n.png');
}
#persona-management-button .drawer-icon::before {
    background-image: url('https://files.catbox.moe/d6ou3n.png');
}
#rightNavHolder .drawer-icon::before {
    background-image: url('https://files.catbox.moe/d6ou3n.png');
}
#table_database_settings_drawer .drawer-icon::before {
    background-image: url('https://files.catbox.moe/d6ou3n.png');
}


.drawer-icon {
    opacity: 1 !important;
    font-size: calc(var(--icon-size) * 1.1);
    text-shadow: none !important;
    transition: color 0.2s ease, transform 0.2s ease;
}


/* --- 替换“停止生成”按钮的图标 --- */
.mes_stop .fa-circle-stop::before {
    content: '';
    opacity: 0;
}

.mes_stop::before {
    content: '';
    display: block;
    width: 35px; 
    height: 35px;
    background-image: url('https://files.catbox.moe/8mw0i2.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}


/* --- 替换“发送”按钮的图标  --- */
#send_but {
    position: relative;
    font-size: 0 !important; 
}

#send_but::before {
    content: '';
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;

    background-image: url('https://files.catbox.moe/axtbzp.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}


#send_but:hover {
    /* 将背景、边框和文字颜色恢复为默认状态 */
    background-color: rgba(255, 255, 255, 0.2);
    border-color: var(--border-color);
    color: #536F98;

    /* 移除位移和阴影效果 */
    transform: none;
    box-shadow: none;
}



/* --- .fa-bars 图标替换与点击旋转效果 --- */
.fa-bars:before, 
.fa-navicon:before {
    content: ''; 

    background-image: url('https://files.catbox.moe/ap1rgw.png');
    width: 1.2em; 
    height: 1.2em; 

    background-size: contain; 
    background-repeat: no-repeat; 
    background-position: center; 

    display: inline-block;

}


/* --- .fa-magic-wand-sparkles 图标替换 --- */
.fa-magic-wand-sparkles:before, 
.fa-wand-magic-sparkles:before {
    content: '';

    background-image: url('https://files.catbox.moe/xn9xda.png');
    width: 1.2em;
    height: 1.2em;

    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;

    display: inline-block;
    vertical-align: middle; 
}


/* ===== mes_text美化 ===== */

:root {
    /* 主题  */
    --letter-bg: #FDF7F9;             
    --letter-text-color: #7A666F;      

    /* 阴影与装饰色  */
    --letter-shadow-outer-1: rgba(122, 102, 111, 0.12); 
    --letter-shadow-outer-2: rgba(122, 102, 111, 0.08); 
    --letter-shadow-inner: rgba(122, 102, 111, 0.15); 

    --ribbon-color: rgba(224, 202, 211, 0.7); 
    --line-color: rgba(227, 213, 218, 0.8);   
}

/* --- 合并与调整)--- */
.mes_text {
    position: relative;
    background-color: var(--letter-bg);

    /* --- 行距与横线对齐 --- */
    line-height: 2em; 
    /* 精细调整背景纵向偏移*/
    background-position-y: 0.9em; 

    /* --- 横线背景 --- */
    background-image: repeating-linear-gradient(
        transparent,
        transparent calc(2em - 1px),
        var(--line-color) calc(2em - 1px),
        var(--line-color) 2em
    );
    background-size: 100% 2em;

    /* --- 边框与阴影 --- */
    border: none;
    border-radius: 0;
    box-shadow: 
        inset 0 0 0 1px var(--letter-shadow-inner),
        0px 2px 4px var(--letter-shadow-outer-2),
        0px 8px 20px var(--letter-shadow-outer-1);

    /* --- 布局与间距 --- */
    margin: 0 10px 0px 0px; 
    padding: 15px 15px;
}

/* --- 段落间距控制 --- */
.mes_text p {
    margin-top: 0;
    margin-bottom: 2em; 
}

/* 移除最后一个段落的下边距 */
.mes_text p:last-child {
    margin-bottom: 0;
}


/* --- 侧边缎带装饰--- */
.mes_text::before,
.mes_text::after {
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    width: 15px;
    background-image: 
        repeating-linear-gradient(
            45deg,
            var(--ribbon-color),
            var(--ribbon-color) 2px,
            transparent 2px,
            transparent 15px
        ),
        repeating-linear-gradient(
            -45deg,
            var(--ribbon-color),
            var(--ribbon-color) 2px,
            transparent 2px,
            transparent 15px
        );
}

.mes_text::before {
    left: -5px; 
}

.mes_text::after {
    right: -5px;
}

/* ===== 消息界面美化END ===== */


/* ===== 全局BEGIN ===== */

/* --- 滚动条 --- */

::-webkit-scrollbar {
    width: 6px; 
    background-color: transparent; 
}

::-webkit-scrollbar-track {
    background-color: rgba(224, 202, 211, 0.2); 
    border-radius: 10px; 
}

::-webkit-scrollbar-thumb {
    background-color: rgba(224, 202, 211, 0.8); 
    border-radius: 10px; 
    border: 1px solid rgba(255, 255, 255, 0.5); 
}

::-webkit-scrollbar-thumb:hover {
    background-color: #7A666F; 
}

*{
  scrollbar-width: thin; 
  scrollbar-color: #E0CAD3 #F8F0F2; 
}



/* --- 输入区域 --- */
#form_sheld {
    background: transparent !important;
    border: none !important;
    box-shadow: none !important;
    padding: 0!important;
}


#send_form {
    background-color: rgba(253, 247, 249, 0.9);
    border-radius: 18px;
    border: 1px solid rgba(224, 202, 211, 0.7);
    box-shadow: 0 2px 10px rgba(122, 102, 111, 0.1);
    margin: 0 auto !important;
    display: flex;
    align-items: flex-end; 
    padding: 5px 5px !important;
    transition: all 0.3s ease-in-out;
}


#send_textarea {
    background: transparent !important;
    border: none !important;
    box-shadow: none !important;
    color: var(--letter-text-color, #7A666F);
    padding: 5px 5px !important;
    resize: none;
}


#send_textarea::placeholder {
    color: rgba(122, 102, 111, 0.6);
    font-style: italic;
}

#send_form form .file_attached {
    background-color: rgba(253, 247, 249, 0.9);
    border: 1px dashed rgba(224, 202, 211, 0.8);
    border-radius: 16px;
    color: var(--letter-text-color, #7A666F);
    margin-bottom: 5px;
}


/* ===== 面板输入 ===== */

.text_pole,
input[type="text"],
input[type="number"],
textarea,
select {
    background-color: rgba(253, 247, 249, 0.5); 
    backdrop-filter: blur(4px); 
    border: 1px solid rgba(255, 255, 255, 0.4); 
    border-radius: 12px; 
    box-shadow: 0 4px 15px rgba(122, 102, 111, 0.1); 
    color: var(--letter-text-color, #7A666F); 
}

.text_pole:focus,
input[type="text"]:focus,
input[type="number"]:focus,
textarea:focus,
select:focus {
    outline: none;
    background-color: rgba(253, 247, 249, 0.7); 
    border-color: rgba(224, 202, 211, 1); 
    box-shadow: 0 0 0 3px rgba(224, 202, 211, 0.5), 
                0 4px 15px rgba(122, 102, 111, 0.1); 
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header, 
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
.inline-drawer-header,
#user-settings-block h4, 
.standoutHeader {
    background-image: url('https://files.catbox.moe/wkhv4n.jpg');
    background-size: cover;  
    background-position: center;  
    background-repeat: no-repeat;  
    margin-top: 0.3125rem;  /* 5px */
    margin-bottom: 0.3125rem; /* 5px */
    border-radius: 0.625rem; /* 10px */
    padding: 0.3125rem; /* 5px */
    transition: all 250ms;
    border: none;
}

.inline-drawer-header .inline-drawer-icon {
    color: #E0CAD3; 
    font-size: 1.2em !important; 
    transition: transform 0.3s ease-in-out; 
}

.inline-drawer-header .inline-drawer-icon.up {
    transform: rotate(180deg);
}

/* ===== 面板选框 ===== */
input[type="checkbox"],
input[type="radio"] {
    -webkit-appearance: none;
    appearance: none;
    margin: 0;
    width: 1em; 
    height: 1em;
    border-radius: 4px; 
    transform: translateY(-0.075em);
    cursor: pointer;
    position: relative;
    transition: all 0.2s ease-in-out;

    background-color: rgba(253, 247, 249, 0.5);
    border: 1px solid rgba(255, 255, 255, 0.6);
    box-shadow: 0 1px 3px rgba(122, 102, 111, 0.1);
}

input[type="radio"] {
    border-radius: 50%; 
}

input[type="checkbox"]:checked {
    background-color: #E0CAD3; 
    border-color: #D4B6C3;
}

input[type="radio"]:checked {
     border-color: #D4B6C3;
}

/* ===== 按钮美化  ===== */
.menu_button, button, .api_button {
    background-color: #FEFBFB; 
    color: #8C737E; 
    border: 1px solid #EAD9DF; 
    border-bottom: 3px solid #E0CAD3;
    border-radius: 12px; 
    padding: 2px 6px; 
    font-weight: bold; 
    text-shadow: 0 1px 1px #FFFFFF;
    box-shadow: 0 2px 5px rgba(122, 102, 111, 0.1); 
    cursor: pointer;
    transition: all 0.15s ease-out; 
    outline: none; 
    position: relative;
    top: 0; 
}

.menu_button:hover, button:hover, .api_button:hover {
    background-color: #FFFFFF; 
    color: #7A666F; 
    top: -1px; 
    box-shadow: 0 4px 10px rgba(122, 102, 111, 0.2); 
}


.redWarningBG, .red_button {
    background-color: #FADADD!important; 
    color: #C7526E!important; 
    border-color: #F6B8C7!important;
    border-bottom-color: #F295A3!important;
}
.redWarningBG:hover, .red_button:hover {
    background-color: #FCE4E8!important;
    color: #AE3854!important;
}


/* ===== 滑块与滑轨 ===== */

.neo-range-slider {
    -webkit-appearance: none; 
    appearance: none;         
    width: 100%;
    background: transparent; 
    cursor: pointer;
}

.neo-range-slider::-webkit-slider-runnable-track {
    height: 8px; 
    background: #F8F0F2; 
    border-radius: 10px; 
    box-shadow: inset 0 1px 3px rgba(122, 102, 111, 0.2);
}

.neo-range-slider::-moz-range-track {
    height: 8px;
    background: #F8F0F2;
    border-radius: 10px;
    box-shadow: inset 0 1px 3px rgba(122, 102, 111, 0.2);
    border: none;
}

/* --- Chrome/Safari 滑块样式 --- */
input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 80px; 
    height: 80px;  
    top: -10px;
    background-image: url('https://files.catbox.moe/bjtnsy.png');
    background-size: contain; 
    background-repeat: no-repeat;
    background-position: center;
    background-color: transparent; 
    border-radius: 0; 
    box-shadow: none; 
    border: none; 
    cursor: grab;
}

/* --- Firefox 滑块样式 --- */
input[type="range"]::-moz-range-thumb {
    width: 80px; 
    height: 80px; 
    top: -10px;
    background-image: url('https://files.catbox.moe/bjtnsy.png');
    background-size: contain; 
    background-repeat: no-repeat;
    background-position: center;
    background-color: transparent; 
    border-radius: 0; 
    box-shadow: none; 
    border: none; 
    cursor: grab;
}



/* --- 侧滑菜单/抽屉面板 --- */
.drawer-content,
#right-nav-panel,
#WorldInfo,
#user-settings-block,
#character_popup,
#dialogue_popup,
.popup .popup-body, 
.modal-dialog,
#advanced_char_def, 
.dropdown-menu,
#form_role_play,
#lorebook-popup {

    background-color: rgba(253, 247, 249, 0.88); 
    backdrop-filter: blur(12px); 
    -webkit-backdrop-filter: blur(12px); 

    border: 1px solid rgba(255, 255, 255, 0.5); 
    border-radius: 10px; 
    box-shadow: 0 8px 32px 0 rgba(122, 102, 111, 0.2); 


}

.drawer-content hr,
#character_popup hr,
.popup-body hr {
    border: none;
    height: 1px;
    background-color: rgba(224, 202, 211, 0.4);
    margin: 15px 0; 
}

.drag-grabber {
    color: rgba(224, 202, 211, 0.8) !important;
    text-shadow: 0 1px 2px rgba(255, 255, 255, 0.5);
    transition: color 0.2s ease;
}
.drag-grabber:hover {
    color: #E0CAD3 !important;
}

#rm_group_chats_block, 
#rm_ch_create_block, 
#rm_characters_block,
.right_menu {
    background: transparent;
}

#dialogue_popup, .popup {
    padding: 0;
    box-shadow: none;
    border-radius: 10px; 
}

#chat {
    backdrop-filter: none; 
