/* 引入字体 */
@import url('https://fonts.googleapis.com/css2?family=Kalam:wght@300;400;700&display=swap');
@import url("https://static.zeoseven.com/zsft/294/main/result.css");
@import url("https://static.zeoseven.com/zsft/223/main/result.css");

/* 主设置 */
body {
    font-family: 'Kalam', cursive, "LXGW WenKai TC", "huangkaihuaLawyerfont", serif;
    color: #2c3e50;
    line-height: 1.8; 
    font-size: 18px; 
}

/* 设置字体优先级 */
h1, h2, h3, p {
    font-family: 'Kalam', cursive, "LXGW WenKai TC", serif;
}

/* 段落文本 */
p {
    color: #584f1d; 
    line-height: 1.8; 
}

/* 加粗文本 */
strong {
    color: #50642f;
    font-weight: 700;
    font-size: 1.02em; 
}

/* 引用文本 */
q {
    background-color: rgba(180, 210, 190, 0.4); 
    background: linear-gradient(45deg, rgba(180, 210, 190, 0.8), rgba(210, 210, 168, 0.8));
    padding: 2.5px 6px; 
    border-radius: 25px;
    font-size: 1.05em;
}

/* 三反引号包裹的文字样式 */
pre {
    background-color: #f5f2e9; 
    color: #333333;
    padding: 5px;
    font-size: 20px;  
    letter-spacing: 1.2px; 
    border: 1px solid rgba(145, 170, 157, 0.5); 
    border-radius: 2px; 
    background-image: 
        linear-gradient(rgba(222, 214, 196, 0.3) 1px, transparent 1px),
        linear-gradient(90deg, rgba(222, 214, 196, 0.3) 1px, transparent 1px);
    background-size: 20px 20px;
    box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1); 
    position: relative;
    line-height: 2; 
}

pre::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="200" height="200" opacity="0.03"><circle cx="100" cy="100" r="70" stroke="%23000" fill="none"/></svg>');
    opacity: 0.07; 
}

/* 双反引号包裹的文字样式 */
code {
    color: #004d00; 
    background: linear-gradient(to bottom, rgba(219, 229, 232, 0.3), rgba(205, 218, 225, 0.3));
    line-height: 1.7; 
    font-size: 17px;  
    letter-spacing: 0.8px; 
    padding: 3px 6px;
    border-radius: 4px;
    position: relative;
    border: 1px solid rgba(169, 190, 199, 0.2);
    font-family: "Kalam", "huangkaihuaLawyerfont", serif !important;
}

/* 禁用 .hljs 的背景颜色和文字颜色 */
.hljs {
    background-color: transparent !important;
    color: inherit !important;
}

/* 按钮 */
.menu_button:not(.mes_edit_buttons *) {
  border-color: var(--ui-color-sec); 
}

.mes {
  flex-direction: column;
  border: none !important;
}

#sheld {
  top: calc(var(--topBarBlockSize) + var(--top-distance));
  height: calc(100dvh - var(--topBarBlockSize) - var(--top-distance) - 1px);
}

#chat {
  overflow-y: scroll;
  align-items: center;
  max-height: calc(100dvh - var(--topBarBlockSize) - 20px) !important;
  padding: calc(var(--scroll-width) + 4px) 0 var(--scroll-width)
    var(--scroll-width);
  border-radius: 0 0 var(--box-border-radius) var(--box-border-radius);
  background-color: rgba(200, 200, 180, 0.3); 
}

/* 缩小头像 */
.mesAvatarWrapper .avatar {
  margin: calc(var(--mainFontSize)) 0 calc(var(--mainFontSize) * 0.4) 0;
  width: 50px !important; /* 设置头像宽度 */
  height: 50px !important; /* 设置头像高度 */
}

/* 重新排列消息元数据 */
#chat .ch_name {
  position: relative;
  display: flex;
  align-items: center;
  gap: 8px;
  padding: calc(var(--mainFontSize) * 0.5) 10px;
  background-color: rgba(220, 220, 210, 0.3);
  border-radius: 4px;
  margin-bottom: 5px;
  font-size: 0.9em;
}

/* 消息楼层样式 */
.mes_floornum {
  margin-right: 5px;
  color: #757575;
}

/* 消息字数统计样式 */
.mes_charcount {
  color: #757575;
}

/* 消息时间样式 */
.mes_date {
  margin-left: auto; /* 将时间推到右侧 */
  color: #757575;
}

/* 消息ID样式 */
.mes_id {
  font-size: 0.8em;
  color: #999;
  margin-left: 5px;
}

/* 消息内容区域 */
.mes_text {
  padding: calc(var(--mainFontSize) * 0.8) 0 0 0;
}

.mes_block {
  width: var(--mes-block-width);
  padding-left: 0;
}

/* ---滚动条--- */
::-webkit-scrollbar {
  width: var(--scroll-width);
}

::-webkit-scrollbar:horizontal {
  height: var(--scroll-width);
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb:vertical,
::-webkit-scrollbar-thumb:horizontal {
  background-color: color-mix(in srgb, var(--theme-color-1) 70%, transparent);
  box-shadow: none;
  border: 0;
}

/* ---输入框--- */
input[type="text"],
input[type="number"],
input[type="switch"],
input:not([type]),
textarea:not([type="search"]) {
  background-color: var(--ui-color-sec) !important;
  border: 1px solid var(--ui-color-sec) !important;
}

::ree {
    lei: qiu *06
}

input[type="text"]:focus,
input[type="number"]:focus,
input[type="switch"]:focus,
input:not([type]):focus,
textarea:not([type="search"]):focus {
  border: 1px solid var(--theme-color-1) !important;
}

textarea[type="search"] {
  background-color: transparent !important;
  border: none !important;
}

.range-block {
  margin: calc(var(--mainFontSize) * 0.6) 0;
}

.range-block-counter {
  margin-left: 20px;
}

.text_pole:not(.objective-task) {
  background-color: var(--ui-color-sec);
  border: none;
}

/* 覆盖样式 */
.inline-drawer-header,
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#top-settings-holder h4:not(#ai-config-button *):not(#rightNavHolder *) {
  border-width: 0 0 0 3px !important;
  border-color: transparent !important;
  border-style: solid;
  border-radius: 0;
  background-image: none !important;
  padding-left: 7px;
  border-image: linear-gradient(
      to bottom,
      transparent 25%,
      var(--theme-color-1) 25%,
      var(--theme-color-1) 75%,
      transparent 75%
    )
    1 100%;
}

/* 修正标题没对齐的问题 */
.drawer-content .alignItemsBaseline {
  align-items: center !important;
}

#title_api {
  padding-bottom: 0;
}

#title_api::before,
#Backgrounds h3.margin0.flex2::before,
#rm_extensions_block .margin0.flex1::before,
#rm_button_selected_ch h2::before {
  margin-right: calc(var(--mainFontSize) * 0.4);
}


/* 头像去边框 */
.avatar img,
body.big-avatars .avatar img {
  box-shadow: none;
  border: none;
}

.ree {
    lei: qiu *06
}

:root {
  --mes-block-width: 98%;
  /* 此百分比调整聊天记录文字与气泡左右边框的距离 */
  --top-distance: 5px;
  /* 顶栏与上边沿的距离 */
  --bottom-distance: 5px;
  /* 底栏与下边沿的距离 */
  --chat-send-spacing: 5px;
  /* 聊天界面与发送框的距离 */
  --leftRight-chat-spacing: 10px;
  /* 聊天界面与左右边沿的距离，电脑端专供 */
  --scroll-width: 5px;
  /* 滚动条宽度 */
  --icon-size: var(--mainFontSize);
}

::lee{
    ree: color: #qiu06;
}

.mesAvatarWrapper, .last_mes .mesAvatarWrapper {
    display: flex;
    align-items: center;
    margin-bottom: 10px; 
    padding: 0;
}

.mesAvatarWrapper .mesIDDisplay {
    margin-left: 10px;
}

.mesAvatarWrapper .tokenCounterDisplay {
    margin-left: 10px;
}

.mesAvatarWrapper .mes_timer {
    margin-left: 10px;
}

/* 思维链整体结构 */
.mes_reasoning {
    border-left: none;
    padding-left: 0px;
}

/* 思维链标题块 */
.mes_reasoning_header {
    margin: 0.5em 2px;
    padding: 0.5em;
    padding-right: 1.6em;
    border-radius: 12px;  
    background-color: rgba(255, 255, 255, 0.4);
    box-shadow: 0 0 0 2px rgba(255, 255, 255, 0.4), 
                1px 1px 2px 1px rgba(255,255,255,0.2);
    font-size: 0.8em; 
    align-items: baseline;
    color: #493e04;
}

@media (min-width: 768px) {
  .mes_reasoning_header {
    font-size: 0.8em; 
    padding: 0.5em;
    padding-right: 1em;
    border-radius: 16px;
  }
}

#qr--bar {
  position: absolute !important;
  bottom: 100% !important;
  left: 0 !important;
  right: 0 !important;
  z-index: 1000 !important;

  padding: 10px 14px !important;
  border-radius: 14px;
  border: 1px solid rgba(255, 200, 240, 0.07);
  background-color: rgba(255, 255, 255, 0.01);
  backdrop-filter: blur(22px);
  box-shadow:
    0 4px 12px rgba(255, 200, 255, 0.15),
    inset 0 0 10px rgba(255, 240, 255, 0.1);
  animation: rainbowFade 20s ease-in-out infinite;

  display: flex !important;
  align-items: center;
  gap: 8px;

  max-height: 60px;

  overflow-x: auto;
  overflow-y: auto;

  /* 优化动效 */
  transition: all 0.5s ease;
}

/* 内部按钮容器缩一点点，防止误换行 */
.qr--buttons.qr--color {
  display: flex;
  flex-wrap: wrap;
  gap: 5px;
  padding: 8px;
  justify-content: center;
  align-items: center;
  background: var(--qr--color, rgba(255, 248, 240, 0.7));
  border-radius: 10px;
  backdrop-filter: blur(10px);
  box-shadow: 0 0 6px rgba(0, 0, 0, 0.05);
  width: calc(100% - 6px); 
  box-sizing: border-box;

  overflow: visible;
}

/* 悬停按钮微动效 */
.qr--button-label:hover {
  background-color: #ffe4f0;
  box-shadow:
    0 2px 6px rgba(255, 180, 220, 0.4),
    inset 0 0 4px rgba(255, 180, 220, 0.3);
  transform: translateY(-1px);
}

.qr--button {
  border: none !important;
  box-shadow: none !important;
  background: transparent !important;
}

#send_form {
  position: relative !important;
}

/* 按钮本体 */
.qr--button-label {
  padding: 5px 7px;
  font-size: 12.5px;
  color: rgba(90, 20, 60, 0.92); /* 更深更清晰 */
  border-radius: 10px;
  border: 1px solid rgba(255, 150, 200, 0.8);
  box-shadow:
    0 1px 3px rgba(255, 200, 230, 0.8),
    inset 0 0 4px rgba(255, 180, 220, 0.15);
  background: transparent;
  transition: all 0.3s ease;
  cursor: pointer;
  user-select: none;
  white-space: nowrap;

  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  filter: drop-shadow(0 0 1px rgba(255,255,255,0.4));
}


