/* === 底部按钮图标替换 === */
#options_button,          /* 左侧按钮 */
#extensionsMenuButton,    /* 中间按钮 */ 
#send_but {               /* 右侧发送按钮 */
  position: relative;
  background: none !important;
  min-width: 30px !important;  /* 保证按钮最小宽度 */
}

/* 伪元素容器 */
#options_button::before,
#extensionsMenuButton::before,
#send_but::before {
  content: "" !important;
  display: block !important;
  position: absolute !important;
  top: 50% !important;
  left: 50% !important;
  transform: translate(-50%, -50%) !important;
  width: 30px !important;        /* 图标实际显示尺寸 */
  height: 30px !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
  background-size: contain !important;
  z-index: 10 !important;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1) !important;
}

/* 单独配置每个图标 */
#options_button::before {         /* 左侧图标 */
  background-image: url('https://files.catbox.moe/0jq6sv.png');
  filter: drop-shadow(0 2px 4px rgba(216,191,216,0.2));
}

#extensionsMenuButton::before {   /* 中间图标 */
  background-image: url('https://files.catbox.moe/nseho7.png');
  width: 30px !important;        /* 特殊尺寸调整 */
  height: 30px !important;
}

#send_but::before {               /* 右侧发送图标 */
  background-image: url('https://files.catbox.moe/uopwna.png');
}

/* 悬停动画效果 */
#options_button:hover::before,
#extensionsMenuButton:hover::before,
#send_but:hover::before {
  transform: 
    translate(-50%, -50%)
    scale(1.15)
    rotate(8deg) !important;
  filter: 
    drop-shadow(0 4px 8px rgba(153,102,204,0.3)) 
    brightness(1.1);
}

/* 点击反馈效果 */
#options_button:active::before,
#extensionsMenuButton:active::before,
#send_but:active::before {
  transform: translate(-50%, -50%) scale(0.95) !important;
  transition: all 0.1s ease !important;
}
/* === 通用图标容器设置 === */
