custom_css:
@import url('https://fonts.googleapis.com/css2?family=Outfit:wght@100..900&display=swap');

body {
  font-family: 'Outfit', sans-serif;
}

/* 常规设置 */
/* 滚动条隐藏 */
/*===== 滚动条隐藏 =====*/
#chat,
#sheld,
#form_create,
.scrollableInner,
#rm_api_block,
.drawer-content,
#rm_print_characters_block,
#user-settings-block {
    overflow: auto;
    -ms-overflow-style: none;
    scrollbar-width: none;
}

#chat::-webkit-scrollbar,
#sheld::-webkit-scrollbar,
#form_create::-webkit-scrollbar,
.scrollableInner::-webkit-scrollbar,
#rm_api_block::-webkit-scrollbar,
.drawer-content::-webkit-scrollbar,
#rm_print_characters_block::-webkit-scrollbar,
#user-settings-block::-webkit-scrollbar {
    display: none;
}

/*HotSwap区域*/
#HotSwapWrapper {
    overflow-x: auto;
    overflow-y: hidden;
    flex-wrap: nowrap;
    width: 100%;
    white-space: nowrap;
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: stretch;
}

#HotSwapWrapper>* {
    flex-shrink: 0;
}

/* qr */
#qr--bar.popoutVisible {
    padding-right: 0em !important;
    height: 50px;
    overflow-y: auto;
    border-radius: 20px !important;
}

#qr--bar {
    display: none !important;
    border-radius: 20px !important;
}

#send_form:focus-within #qr--bar {
    display: flex !important;
    border-radius: 20px !important;
}

.list-group.ctx-menu {
    overflow-y: auto;
    overflow-x: hidden;
    height: 300px;
    scrollbar-width: thin;
    scrollbar-color: var(--SmartThemeUnderlineColor) #f1f1f100;
}

.list-group.ctx-menu>* {
    margin-bottom: 3px;
    padding: 10px;
    border-radius: 0px;
}

.list-group.ctx-menu>*:last-child {
    margin-bottom: 0;
}

/*收藏角色样式*/
.character_select.is_fav .avatar {
    outline: 3px solid #F6F6F6;
}

.character_select.is_fav .ch_name {
    color: #F6F6F6;
}



#top-bar {
box-shadow: 0 3px 20px rgba(255, 255, 255, 0.3);
}


/* 聊天区域 */
/* 消息整体布局 */
#chat {
    border: none;
    overflow-x: hidden;
}

#sheld {
  overflow-y: hidden;
}

.mes {
    display: flex;
    flex-direction: column;
    padding: 0px 20px 10px 20px;
    gap: var(--size-0);
    border: none;
}

.flex-container {
    padding: 0px !important;
    overflow-x: visible;
    overflow-y: visible;
}

.mes>.mesAvatarWrapper,
.mes>.mes_block {
    width: 100%;
    max-width: 100%;
    box-sizing: border-box;
}

.mes_text {
    padding: 0 10px 10px 10px;
}

.last_mes .mesAvatarWrapper {
    padding-bottom: 0px;
}

body.bubblechat .mes {
    border: none;
    border-radius: 0px;
    padding: 35px;
}


/* 消息框 */

/* 头像区域 */
.mes[is_user="false"] .mesAvatarWrapper {
    background-color: var(--SmartThemeBlurTintColor);
    height: 60px;
    border-radius: 10px;
    padding: 0px 10px 0px 10px;
    margin: 10px 0 15px;
    display: flex;
    align-items: center;
}

/* 基础头像容器 - 建立定位上下文 */
.avatar {
    position: relative; /* 关键：为装饰框建立定位上下文 */
    display: inline-block;
    margin: 0 0 0 0;
    z-index: 1;
}

/* 消息中的头像样式 */
.mes .avatar {
    margin: 0 ;

}
.mes[is_user="true"] .avatar {
    top: 30px;
    left: 10px;
}

/* 头像图片 */
.mes .avatar img {
    border-radius: 50%;
    object-fit: cover;
    position: relative;
    z-index: 2;
}

/* ========== 用户头像装饰框 ========== */
.mes[is_user="true"] .avatar::before {
    content: '';
    position: absolute;
    top: -15.3%; 
    left: -0.4%; 
    width: 120%; 
    height: 120%; 
    background-image: url('https://i.postimg.cc/fyZHzDJ7/Group-1187517196.png');
    background-size: contain;
    background-position: left;
    background-repeat: no-repeat;
    z-index: 3;
    pointer-events: none;
}

/* ========== 角色头像装饰框 ========== */
.mes[is_user="false"] .avatar::before {
    content: '';
    position: absolute;
    top: -10%;
    left: 13%;
    width: 35%; 
    height: 35%; 
    background-image: url('https://i.postimg.cc/FzmPrN7y/Group-1187517154.png');
    background-size: contain;
    background-position: left;
    background-repeat: no-repeat;
    z-index: 3;
    pointer-events: none;
}

@media screen and  (min-width: 400px) and (max-width: 450px) {
  .mes[is_user="false"] .avatar::before {
    top: -10% ;
    left: 40px;
  }
}
/* 思考区域 */
.mes_reasoning_header {
    display: block;
    background-color: #4E4747;
    padding: 1px 20px 1px 5px !important;
    border-radius: 20px;
     color: #767676;

}



/* ==========消息区域 ========== */
 .mes_block {
    background-color: color-mix(in srgb, var(--SmartThemeBlurTintColor) 90%, transparent); 
    backdrop-filter: 
        blur(30px) 
        saturate(200%) 
        brightness(1.1) 
        contrast(1.2);
    border-radius: 18px;
    box-sizing: border-box;
    padding: 10px 10px 55px 10px;
    margin: 0px 0px 20px 0px;

}

/* 消息元数据显示 */
#chat .mes .mesIDDisplay {
  position: absolute !important;
  bottom: 65px;
  left: 32%;
  color: var(--art-deco-green);
  opacity: 0.4;
  z-index: 10;
  font-size: 10px;
}

#chat .mes .mes_timer {
  position: absolute !important;
  top: 35px;
  right: 10%;
  transform: translateX(-10%);
    background-image: url("https://i.postimg.cc/8cDypNjB/Group-1187517197.png");
    background-size: 14px;
    background-repeat: no-repeat;
    padding: 0px 0px 0px 20px;
  letter-spacing: 1px;
  color: var(--SmartThemeBodyColor);
  opacity: 0.7;
  z-index: 10;
    font-size: calc(var(--mainFontSize) * 0.7);

}

#chat .mes .tokenCounterDisplay {
  position: absolute !important;
  bottom: 65px;
  left: 10%;
  opacity: 0.8;
  z-index: 10;
  font-size: 10px;
  min-width: 5px !important; 
  min-height: 13px;
  opacity: 0.4;
}

#chat .mes .tokenCounterDisplay:empty {
  width: 20px;
  height: 13px;
  background: transparent; 
}

#chat .mes[is_user="false"] .tokenCounterDisplay::before {
  content: 'Followers·';
  position: absolute;
  left: 100%; 
  white-space: nowrap;
  padding-right: 10px; 
  transform: none; 
}

#chat .mes[is_user="false"] .mesIDDisplay::before {
  content: 'Following';
  position: absolute;
  left: 100%; 
  white-space: nowrap;
  padding-right: 10px; 
  transform: none; 
}

#chat .mes[is_user="true"] .tokenCounterDisplay::before {
  content: 'Followers·';
  position: absolute;
  left: 100%; 
  white-space: nowrap;
  padding-right: 10px; 
  transform: none; 
  font-size: 10px;
  pointer-events: none !important; 

}

#chat .mes[is_user="true"] .mesIDDisplay::before {
  content: 'Following';
  position: absolute;
  left: 100%; 
  white-space: nowrap;
  padding-right: 10px; 
  transform: none; 
  font-size: 10px;
}




/* 重写名字定位 */

.mes {
  position: relative !important;
  overflow: visible !important;
}


.mes_block {
  overflow: visible !important;
}


#chat .mes .ch_name {
  overflow: visible !important;
}


#chat .mes[is_user="false"] .alignItemsBaseline {
  transform: translate(60px, -80px) !important;
  z-index: 100 !important;
  display: flex;
    flex-flow: row wrap;
    align-items: baseline;

}

#chat .mes[is_user="true"] .alignItemsBaseline {
  transform: translate(60px, -10px) !important;
  z-index: 100 !important;
  display: flex;
    flex-flow: row wrap;
    align-items: baseline;

}
.timestamp {
  flex: 0 0 100%; 
  margin-top: 2px;
  letter-spacing: 1px;
}


/* 应用自定义图标 */
.mes .mes_button.extraMesButtonsHint::before {
    content: "Show more";
    transform: scale(2);
   font-size: 10px;
    font-weight: 700;
    font-family: inter; 
    right: -10px;
}

.mes .mes_button.mes_edit::before {
    content: "Follow";
  font-size: 10px;
    transform: scale(2);
    font-weight: 700;
    font-family: inter; 
    right: -10px;
}

.mes .mes_button.mes_edit {
    border: 1px solid #F6F6F6;
    border-radius: 10px;
}

/*左右翻页按钮样式*/
.swipe_right {
    font-size: 0 !important;
    display: inline-block;
    width: 10px;
    height: 10px;
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    cursor: pointer;
    opacity: 1 !important;
    background-image: url('https://i.postimg.cc/mkwmhJHH/Star-9.png');
}

.swipe_left {
    font-size: 0 !important;
    display: inline-block;
    width: 10px;
    height: 10px;
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    cursor: pointer;
    opacity: 1;
    background-image: url('https://i.postimg.cc/mkwmhJHH/Star-9.png');
}

.swipe_left:hover,
.swipe_right:hover {
    opacity: 0.8;
    outline: none;
}

.swipeRightBlock {
    transform: translate(-25px, -25px) !important;
}

.swipe_left:hover,
.swipe_left{
    transform: translate(20px, -25px) !important;
}

.swipes-counter {
  font-size: 10px;
}

/* 首页 */
.mes.smallSysMes .mes_block {
    background-color: transparent !important;
}

.mes[ch_name="Assistant"],
.mes.smallSysMes .mesAvatarWrapper {
    display: none !important;
}

.welcomeRecent .recentChatList .recentChat{
    background-color: color-mix(in srgb, var(--SmartThemeBlurTintColor) 60%, transparent); 
 backdrop-filter: blur(1000px);
}

/*===== 输入框样式 =====*/
#nonQRFormItems {
    background-color: #58545430;
    backdrop-filter: blur(2px); 
    border-radius: 20px !important;
    border: thin solid #F6F6F6;  
    padding: 10px;
}
#send_textarea {
    background-color: #F6F6F6;
    border-radius: 10px;
    border:none;
    color:#585454;
    clip-path: none;  
}


#form_sheld {
    display: flex;
    justify-content: center;
}

#send_form {
    position: relative !important;
    border-radius: 20px !important;
    border: 1px solid #515F76;
    background-color: #58545430;
    backdrop-filter: blur(2px); 
    margin: 0px 0px 20px 0px;
    width: 90%;
}
/* 角色卡界面 */
/*角色菜单*/
.character_select.entity_block.flex-container.wide100p.alignitemsflexstart.interactable{
    padding: 5px  !important;
}
/*角色详情界面布局*/
#avatar_div {
flex-direction: column;
}

#avatar_controls {
align-items: center;
}

#form_create {
padding: 0px 15px 5px 15px;
}

/* ===== 表单控件样式 ===== */
/* 小铅笔内容 */
.reasoning_edit_textarea,
.edit_textarea {
  background-color: color-mix(in srgb, var(--SmartThemeBlurTintColor) 80%, black 20%);
    border: none !important;
}

/* 滑块图标 */
input[type="range"]::-webkit-slider-thumb {
    background-color: #7CABFF;
    cursor: pointer;
    box-shadow: none;
    border: none !important;
}
/* 勾选框 */
input[type='checkbox'] {
border-radius: 50px;
    border: none !important;
    background-color: var(--SmartThemeBlurTintColor);
    filter: none !important;
    box-shadow:none;
}

input[type="checkbox"]::before {
    width: 1em;
    height: 1em;
    background-color: #7CABFF;
    border-radius: 50%; 
    transform-origin: center; 
    box-shadow: none; 
    clip-path: none; 
    border: none !important;
    box-shadow:none;
}

/*===== 界面标题与头部样式 =====*/
