custom_css:
/*
 * =======================================
 * 主题: 黛峦絮语
 * 作者: （*＾-＾*）
 * 注：修改手机端头像等需要在“13. 移动端”修改
 * =======================================
 */


/* --- 1. 全局样式 --- */

/* 1.1 导入并应用自定义字体 */
@import url("https://fontsapi.zeoseven.com/482/main/result.css");

body,
textarea,
input,
select,
button,
.mes_text,
.ch_name {
    font-family: "CooperZhengKai", sans-serif !important;
    font-size: 18px !important; /* 全局基础字体大小 */
}

/* 改字体截止↑ 记得同步修改11.5*/

[class*="fa-"] {
    font-family: "Font Awesome 6 Free", "Font Awesome 6 Brands", "Font Awesome 6 Solid" !important;
}

/* 1.2 禁用全局阴影和滤镜 */
* {
    box-shadow: none !important;
    text-shadow: none !important;
    filter: none !important;
}


/* --- 2. 界面布局与背景 --- */

/* 2.1 顶栏 */
#top-bar {
    background-image: url('https://iili.io/KhOyvdg.png') !important;
    background-size: cover !important;
    background-position: center !important;
    background-repeat: no-repeat !important;
    border-radius: 0 0 10px 10px !important; /* 圆角大小 */
}

/* 2.2 下拉菜单背景 */
.drawer-content {
    background-image:
        linear-gradient(rgba(255, 255, 255, 0.7), rgba(255, 255, 255, 0.7)),
        url('https://iili.io/KhkWpcJ.png') !important;
    background-size: cover !important;
    background-position: center !important;
    background-repeat: no-repeat !important;
    background-color: transparent !important;
}

/* 2.3 底栏与输入框 */
#form_sheld {
    background: transparent !important;
    border: none !important;
    overflow: visible !important;
    border-radius: 10px 10px 0 0 !important; /* 圆角 */
}

#send_form {
    background: url('https://iili.io/Khgta0N.png') center / cover no-repeat !important;
    border-radius: 10px !important; /* 圆角 */
    margin: 0 auto 3px auto !important; /* 边距：上，右，下，左 */
    width: 100%; /* 宽度 */
    max-width: 950px;
}

#send_textarea {
    background-color: rgba(255, 255, 255, 0.3) !important; /* 输入框底色 */
    border-radius: 10px !important;
    border: none !important;
}


/* --- 3. UI组件 --- */

/* 3.1 输入框、文本域、下拉菜单 */
select,
input[type="text"],
input[type="search"],
input[type="number"],
textarea,
.text_pole {
    background-color: rgba(248, 240, 247, 0.5) !important;
    border: 1px solid #d1c1e8 !important;
    color: #4a356a !important;
    border-radius: 8px !important;
}

select {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    background-image: none !important;
    padding-right: 1em !important;
}

select option {
    background-color: rgba(248, 240, 247, 0.8) !important;
    color: #4a356a !important;
}

/* 3.2 标题栏 */
.standoutHeader,
.inline-drawer-header {
    background: rgba(248, 240, 247, 0.5) !important;
    border: 1px solid #d1c1e8 !important;
    padding: 5px 8px !important;
    border-radius: 8px !important;
    color: #4a356a !important;
}

.title_restorable {
    background: rgba(219, 207, 222, 0.5) !important;
    border: 1px solid #d1c1e8 !important;
    padding: 5px 8px !important;
    border-radius: 8px !important;
    color: #4a356a !important;
}

/* 3.3 Select2 多选框 */
.select2-selection--multiple {
    background-color: rgba(248, 240, 247, 0.5) !important;
    border: 1px solid #d1c1e8 !important;
    border-radius: 8px !important;
}

.select2-selection__choice {
    background-color: #e6d9f2 !important;
    border: 1px solid #c8b7dd !important;
    border-radius: 5px !important;
    color: #4a356a !important;
}

/* 3.4 滑动条 */
input[type="range"]::-webkit-slider-runnable-track,
input[type="range"]::-moz-range-track {
    height: 6px;
    background: #92759E !important;
    border-radius: 3px;
}

input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    margin-top: -9px;
    height: 30px;
    width: 30px;
    border: none;
    cursor: pointer;
    background: url('https://i.postimg.cc/J4DdtHLh/18.png') center / contain no-repeat !important;
}

input[type="range"]::-moz-range-thumb {
    height: 30px;
    width: 30px;
    border: none;
    border-radius: 0;
    cursor: pointer;
    background: url('https://i.postimg.cc/J4DdtHLh/18.png') center / contain no-repeat !important;
}

/* 3.5 特定按钮 */
.flex-container.gap3px > .menu_button.fa-solid {
    background-color: #EBD5DD !important;
    border: 1px solid #d1c1e8 !important;
    color: #4a356a !important;
    border-radius: 6px !important;
}

/* 3.6 禁用面板 */
#instructSettingsBlock.disabled,
#InstructSequencesColumn.disabled {
    background-color: rgba(253, 252, 253, 0.7) !important;
    border: none !important;
    padding: 10px;
    border-radius: 8px;
    margin-top: 10px;
}

/* 3.7 预设面板 */
#completion_prompt_manager_list {
    background: rgba(248, 240, 247, 0.5) !important;
    border: 1px solid #d1c1e8 !important;
    border-radius: 8px !important;
    color: #4a356a !important;
}

#completion_prompt_manager_list li {
    background: transparent !important;
    border-bottom: 1px solid rgba(209, 193, 232, 0.5) !important;
}

#completion_prompt_manager_list li:last-child {
    border-bottom: none !important;
}

.completion_prompt_manager_list_separator hr {
    border-color: rgba(209, 193, 232, 0.7) !important;
}

.completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name a {
    color: rgba(74, 53, 106, 0.3) !important;
    text-decoration: line-through;
}

.completion_prompt_manager_prompt_disabled .prompt_manager_prompt_tokens {
    color: rgba(74, 53, 106, 0.6) !important;
}

/* “总词符数”颜色 */
#completion_prompt_manager .completion_prompt_manager_header {
    color: #4a356a !important; 
}

/* 3.8 标题栏小图标按钮 */
.title_restorable .menu_button_icon {
    background-color: rgba(255, 255, 255, 0.5) !important;
    border: 1px solid rgba(209, 193, 232, 0.7) !important;
    color: #4a356a !important;
    border-radius: 6px !important;
    padding: 2px 4px !important;
    margin-left: 4px !important;
}

.title_restorable .menu_button_icon:hover {
    background-color: rgba(255, 255, 255, 0.8) !important;
}

/* 3.9 普通设置项标题 */
.drawer-content h4:not(.title_restorable) {
    background: none !important;
    border: none !important;
    padding: 0 !important;
    border-radius: 0 !important;
    color: #4a356a !important;
    font-weight: bold !important;
    margin-top: 10px !important;
    margin-bottom: 3px !important;
}

.drawer-content h4:not(.title_restorable)::before {
    content: '➺';
    color: #92759E;
    margin-right: 8px;
    font-weight: bold;
}

/* 3.10 滑动条的数字框 */
input[type="number"].neo-range-input {
    border-radius: 0px 0px 8px 8px !important;
    text-align: center !important;
}

/* 3.11 自定义复选框 */
input[type="checkbox"] {
    -webkit-appearance: none;
    appearance: none;
    margin: 0;
    font: inherit;
    color: currentColor;
    width: 1.15em;
    height: 1.15em;
    border: 1px solid #d1c1e8;
    border-radius: 4px;
    transform: translateY(-0.075em);
    display: grid;
    place-content: center;
    cursor: pointer;
    background-color: rgba(248, 240, 247, 0.5) !important;
}

input[type="checkbox"]::before {
    content: "";
    width: 0.65em;
    height: 0.65em;
    transform: scale(0);
    transition: 120ms transform ease-in-out;
    box-shadow: inset 1em 1em #92759E;
    clip-path: polygon(14% 44%, 0 65%, 50% 100%, 100% 16%, 80% 0%, 43% 62%);
}

input[type="checkbox"]:checked::before {
    transform: scale(1.2);
}

input[type="checkbox"]:checked {
    background-color: rgba(240, 185, 200, 0.6) !important;
    border-color: #92759E !important;
}


/* --- 4. 图标替换 (安全模式) --- */

/* --- 核心图标 --- */

/* 左侧抽屉 */
#leftNavDrawerIcon {
    color: transparent !important;
    background: url('https://iili.io/KhRyDrv.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* API状态 */
#API-status-top {
    color: transparent !important;
    background: url('https://iili.io/Kh5Ry8l.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* 高级格式化 */
#advanced-formatting-button .drawer-icon {
    color: transparent !important;
    background: url('https://iili.io/Kh5SNAQ.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* 世界信息 */
#WIDrawerIcon {
    color: transparent !important;
    background: url('https://iili.io/Kh7d4gs.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* 用户设置 */
#user-settings-button .drawer-icon {
    color: transparent !important;
    background: url('https://iili.io/Kh7YJiF.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* 扩展设置 */
#extensions-settings-button .drawer-icon {
    color: transparent !important;
    background: url('https://iili.io/Khazqdl.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* 角色卡管理 */
#persona-management-button .drawer-icon {
    color: transparent !important;
    background: url('https://iili.io/KhaIxvp.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* 右侧抽屉 */
#rightNavDrawerIcon {
    color: transparent !important;
    background: url('https://iili.io/KhYIzJV.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* 聊天选项 */
#options_button {
    color: transparent !important;
    background: url('https://iili.io/KhcEd3N.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* 聊天扩展菜单 */
#extensionsMenuButton {
    color: transparent !important;
    background: url('https://iili.io/Khawpz7.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* 发送按钮 */
#send_but {
    color: transparent !important;
    background: url('https://iili.io/KhaZvdQ.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 36px !important; /* 保留了自定义高度 */
}

/* 停止生成 */
#mes_stop {
    color: transparent !important;
    background: url('https://iili.io/KhaZO1j.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}


/* --- 兼容性与插件图标 --- */

/* 背景图片 */

/* 旧版 */
#site_logo .drawer-icon {
    background: url('https://iili.io/Kh7lu0F.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}
#site_logo .drawer-icon::before {
    content: ' ' !important;
    visibility: hidden !important;
}

/* 新版 */
.drawer-icon.fa-panorama {
    background: url('https://iili.io/Kh7lu0F.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}
.drawer-icon.fa-panorama::before {
    content: ' ' !important;
    visibility: hidden !important;
}

/* 新版有插件 */
.drawer-icon.fa-image {
    background: url('https://iili.io/Kh7lu0F.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}
.drawer-icon.fa-image::before {
    content: ' ' !important;
    visibility: hidden !important;
}

/* QR助手 */
.fa-star-and-crescent {
    display: inline-block !important;
    vertical-align: middle;
    color: transparent !important;
    background: url('https://iili.io/KhaZ87V.png') center / contain no-repeat !important;
    width: 24px !important;
    height: 24px !important;
}

/* 事件表 (插件) */
#table_drawer_icon {
    color: transparent !important;
    background: url('https://i.postimg.cc/vTSSS0M6/1760846717838.png') center / contain no-repeat !important;
    width: 28px !important;
    height: 28px !important;
}

/* --- 5. 动态与交互图标 --- */

/* 5.1 箭头 */
.fa-solid.fa-circle-chevron-down,
.fa-solid.fa-circle-chevron-up {
    color: transparent !important;
    background-image: url('https://iili.io/Kj2CyeS.png') !important;
    background-size: contain !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
    width: 25px !important;
    height: 25px !important;
    transition: transform 0.3s ease !important;
}

.fa-solid.fa-circle-chevron-up {
    transform: rotate(180deg) !important;
}


/* --- 6. Select2 (下拉框) --- */

/* 6.1 多选框已选项目 */
.select2-container .select2-selection--multiple .select2-selection__choice {
    background-color: #EBD5DD !important;
    border: 1px solid #d1c1e8 !important;
    border-radius: 6px !important;
    transition: background-color 0.2s ease, border-color 0.2s ease !important;
}

.select2-container .select2-selection--multiple .select2-selection__choice__display {
    background-color: transparent !important;
    color: #4a356a !important;
    padding: 2px 5px !important;
}

.select2-container .select2-selection--multiple .select2-selection__choice:hover {
    background-color: #fdeff5 !important;
    border-color: #c8b7dd !important;
}

/* 6.2 多选框删除按钮 */
.select2-container .select2-selection--multiple .select2-selection__choice__remove {
    color: #4a356a !important;
    background-color: transparent !important;
    border: none !important;
    font-weight: bold;
    float: none !important;
    margin: 0 4px 0 6px !important;
    padding: 0 !important;
    transition: color 0.2s ease;
}

.select2-container .select2-selection--multiple .select2-selection__choice:hover .select2-selection__choice__remove {
    color: #ffffff !important;
}

/* 6.3 移除末尾空白 */
.select2-search__field {
    width: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
    border: none !important;
}

/* 6.4 单选框显示区域 */
.select2-selection--single .select2-selection__rendered {
    background-color: rgba(248, 240, 247, 0.5) !important;
    border: 1px solid #d1c1e8 !important;
    color: #4a356a !important;
    border-radius: 8px !important;
    padding: 5px 12px !important;
    line-height: 1.5;
}

.select2-selection--single {
    border: none !important;
    height: auto !important;
    background: transparent !important;
}

.select2-selection--single .select2-selection__arrow b {
    border-color: #4a356a transparent transparent transparent !important;
    margin-top: 2px !important;
    margin-left: -8px !important;
}


/* --- 7. Select2 下拉列表 --- */

/* 7.1 下拉列表面板 */
.select2-dropdown {
    background-color: #f8f1f8 !important;
    border: 1px solid #d1c1e8 !important;
    border-radius: 8px !important;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1) !important;
}

/* 7.2 列表内搜索框 */
.select2-search--dropdown .select2-search__field {
    background-color: #ffffff !important;
    border: 1px solid #d1c1e8 !important;
    border-radius: 6px !important;
    color: #4a356a !important;
    margin: 5px !important;
    width: calc(100% - 10px) !important;
}

.select2-search--dropdown .select2-search__field::placeholder {
    color: rgba(74, 53, 106, 0.6) !important;
}

/* 7.3 列表选项 */
.select2-results__option {
    color: #4a356a !important;
    padding: 8px 12px !important;
    transition: background-color 0.2s ease;
}

.select2-results__option--highlighted {
    background-color: #EBD5DD !important;
}

.select2-results__option--selected {
    background-color: #dccfe0 !important;
    font-weight: bold;
}

.select2-results__option::before {
    display: none !important;
}


/* --- 8. 杂项修复 --- */

/* 8.1 顶栏下拉面板无缝连接 */
#top-settings-holder .drawer-content {
    margin-top: -2px !important; /* 数字大往上 */
    border-top-left-radius: 0 !important;
    border-top-right-radius: 0 !important;
    border-top: 1px solid rgba(209, 193, 232, 0.7) !important;
}

/* 8.2 Regex (正则) 扩展 */
.regex-script-label {
    width: 90% !important; /* 正则宽度，可调整 */
    margin: 0 auto 3px auto !important; /*居中设置，可以删掉 */
    background-color: rgba(248, 240, 247, 0.4) !important;
    border: 1px solid #d1c1e8 !important;
    border-radius: 8px !important;
    padding: 4px 10px !important;
    transition: all 0.2s ease;
}

/* 8.3 正则后按钮 */
.regex-script-label .menu_button {
    padding: 1px 3px !important;    /* 按钮内边距 (1上下, 3左右) */
    min-width: 0 !important;        
    margin: 0 1px !important;         /* 按钮之间间距 */
    line-height: 1.2 !important;      /* 按钮行高 */
    font-size: 0.7em !important;      /* 按钮图标大小 */
}

/* --- 9. PC滚动条 --- */
:root {
    --scrollbar-width: 0px;
    --scrollbar-thumb-color: rgba(146, 117, 158, 0.6);
    --scrollbar-thumb-hover-color: rgba(146, 117, 158, 0.9);
    --scrollbar-track-color: transparent;
}

::-webkit-scrollbar {
    width: var(--scrollbar-width) !important;
    height: var(--scrollbar-width) !important;
}

::-webkit-scrollbar-track {
    background: var(--scrollbar-track-color) !important;
    border-radius: var(--scrollbar-width) !important;
}

::-webkit-scrollbar-thumb {
    background-color: var(--scrollbar-thumb-color) !important;
    border-radius: var(--scrollbar-width) !important;
    border: none !important;
    transition: background-color 0.2s ease !important;
}

::-webkit-scrollbar-thumb:hover {
    background-color: var(--scrollbar-thumb-hover-color) !important;
}

::-webkit-scrollbar-corner {
    background: transparent !important;
}


/* --- 10. 聊天面板PC端 --- */

/* 10.1 消息块基础布局 */
#chat .mes {
    display: flex !important;
    flex-direction: column !important;
    align-items: center !important;
    margin-bottom: 50px !important;
    background: none !important;
    border: none !important;
}

/* 10.2 头像所在行背景板 */
.mes .mesAvatarWrapper {
    width: 95% !important; /* 宽度 */
    margin: 0 auto 15px auto !important; /* 与消息间距 */
    position: relative !important;
    background-image: url('https://i.postimg.cc/dQTd9Ly7/image.png') !important;
    background-size: 100% 100%;
    border-radius: 40px !important; /* 圆角大小 */
    height: 180px; /* 背景高度 */
}

/* 10.3 头像框 */
.mes .mesAvatarWrapper::after {
    content: '' !important;
    display: block;
    position: absolute !important;
    z-index: 3;
    background-image: url('https://i.postimg.cc/k5XJGcHz/4.png') !important;
    background-size: 100% 100%;
    pointer-events: none;
    width: 380px !important;
    height: 210px !important;
    top: -55px; /* 负数上移 */
    left: 67%; /* 右移67 */
    transform: translateX(-50%);
}

/* 10.4 头像 */
.mes .avatar {
    position: absolute !important;
    z-index: 2;
    width: 200px !important;
    height: 130px !important;
    top: -55px;
    left: 67%;
    transform: translateX(-50%) translate(85px, 75px);
}

.mes .avatar img {
    width: 100% !important;
    height: 100% !important;
    object-fit: cover;
}

/* 10.5 聊天内容气泡 */
.mes .mes_block {
    width: 100% !important;
    margin: 0 auto !important;
    border-radius: 20px !important;
    padding: 15px !important; /* 气泡内边距 */
    background-color: var(--SmartThemeBotMesBlurTintColor) !important;
}

.mes[is_user="true"] .mes_block {
    background-color: var(--SmartThemeUserMesBlurTintColor) !important;
}

/* 10.6 楼层/计时/字符信息 */
.mes .mesIDDisplay {
    position: absolute !important;
    z-index: 5 !important;
    color: #4a356a;
    font-size: 0.8em;
    bottom: 10px; /* 与底边距离 */
    left: calc(15% - 70px); /*左移70 */
}

.mes .mes_timer {
    position: absolute !important;
    z-index: 5 !important;
    color: #4a356a;
    font-size: 0.8em;
    bottom: 10px;
    left: 14%;
    transform: translateX(-50%);
}

.mes .tokenCounterDisplay {
    position: absolute !important;
    z-index: 5 !important;
    color: #4a356a;
    font-size: 0.8em;
    bottom: 10px;
    left: calc(50% - 210px);
}

/* 10.7 聊天区容器 */
#chat {
    background: transparent !important;
    padding-top: 50px !important; /*第一条消息与顶栏距离 */
    padding-left: 0 !important;
    padding-right: 0 !important;
}

/* 10.8 最后一条消息与底栏的距离 */
#chat .mes:last-child {
    margin-bottom: 20px !important;
}


/* --- 11. 聊天文本样式 --- */

/* 11.1 用户名与时间戳 */
.mes .mes_block .ch_name {
    width: 100% !important;
    box-sizing: border-box !important;
    margin: 0 !important;
    padding: 0 0 10px 0 !important;
    margin-bottom: 10px !important;
    border-bottom: 1px solid rgba(209, 193, 232, 0.5) !important;
}

.mes .ch_name .alignItemsBaseline {
    flex-wrap: wrap !important;
}

.mes .ch_name .timestamp {
    width: 100% !important;
    margin-top: 4px !important;
    padding-left: 2px !important;
}

.mes .ch_name .name_text {
    font-weight: bold;
    font-size: 1.1em;
}

/* 11.2 聊天正文 */
.mes .mes_block .mes_text {
    width: 100% !important;
    box-sizing: border-box !important;
    margin: 0 !important;
    padding: 0 !important;
    text-align: justify !important;
    line-height: 1.5 !important; /* 行距 */
}

/* 11.3 思维链 (Reasoning) */
.mes_reasoning_header {
    position: relative !important;
    background-color: rgba(219, 207, 222, 0.6) !important;
    border: 1px solid #d1c1e8 !important;
    border-radius: 8px !important;
    padding: 5px 30px 5px 10px !important;
}

.mes_reasoning_header_title {
    font-size: 0 !important;
}

.mes_reasoning_header_title::after {
    content: '半缕藤花绕絮语'; /* 可更改 */
    font-size: 1rem;
    color: #4a356a;
}

.mes_reasoning_arrow {
    position: absolute !important;
    top: 50% !important;
    right: 10px !important;
    transform: translateY(-50%) rotate(180deg) !important;
    color: #4a356a !important;
    transition: transform 0.3s ease !important;
}

details[open] .mes_reasoning_arrow {
    transform: translateY(-50%) rotate(0deg) !important;
}

/* 11.4 消息操作按钮 */
.mes_button {
    font-size: 0.8em !important;
    background: none !important;
    border: none !important;
    box-shadow: none !important;
    color: #92759E !important;
}

.mes_button:hover {
    background: none !important;
    color: #92759E !important;
}

/* 11.5 行内代码 */
.mes_text code:not([class*="hljs"]) {
    font-family: "CooperZhengKai", sans-serif !important;
    background-color: rgba(146, 117, 158, 0.65) !important;
    color: #f5eef8 !important;
    border-radius: 15px !important;
    padding: 2px 4px !important;
    border: 1px solid rgba(209, 193, 232, 0.6) !important;
}

/* 11.7 代码块 */
.mes_text pre {
    background-color: rgba(74, 53, 106, 0.3) !important;
    border: 1px solid #d1c1e8 !important;
    border-radius: 12px !important;
    padding: 12px 15px !important;
    overflow-x: auto !important;
    white-space: pre !important;
}

.mes_text pre code {
    background: none !important;
    padding: 0 !important;
}

/* 11.8 删除消息操作 */

.mes[is_user], .mes:not([is_user]) {
    position: relative !important;
}

.del_checkbox {
    position: absolute !important;
    left: 50px !important; 
    top: 20px !important;
    z-index: 10 !important;
    width: 22px !important;
    height: 22px !important;
    background-color: rgba(255, 255, 255, 0.8) !important;
    border: 2px solid #e11d48 !important;
}
.del_checkbox::before {
    display: none !important;
}
.del_checkbox:checked {
    background-color: #ef4444 !important;
    border-color: #e11d48 !important;
}

.del_checkbox:checked ~ .mes_block {
    background-color: rgba(220, 38, 38, 0.35) !important;
    transition: background-color 0.2s ease-in-out !important;
}


/* --- 12. 全局分割线 --- */
hr {
    border: none !important;
    background-color: transparent !important;
    height: 15px !important;
    width: 100% !important;
    background-image: url('https://i.postimg.cc/zvD3dZ8B/image.png') !important;
    background-size: contain !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
    margin: 5px 0 !important;
    opacity: 1 !important;
    position: relative !important;
    z-index: 1 !important;
}


/*  13. 移动端 */
@media (max-width: 768px) {

    /* 头像区域整体高度 */
    .mes .mesAvatarWrapper {
        height: 220px !important;
    }

    /* 手机版头像框 */
    .mes .mesAvatarWrapper::after {
        background-image: url('https://i.postimg.cc/FFSSwXhW/1760172559116.png') !important;
        top: -45px !important; /* 加大↓，减小↑ */
        left: 65% !important; /* 加大→，减小← */
        width: 60% !important; /* 宽度 */
        height: auto !important;
        aspect-ratio: 925 / 1023;
    }

    /* 调整手机版头像尺寸与位置 */
    .mes .avatar {
        left: 40% !important;
        transform: translateX(-50%) translate(108px, 40%); /* px加大向右，%加大往下 */
        width: 41% !important; /* 宽度 */
        height: auto !important;
        aspect-ratio: 200 / 232;
    }

    /* 调整信息栏位置 */
    .mes .mesIDDisplay {
        left: calc(50% - 140px) !important; /* px加大往左 */
        bottom: 50px !important; /* 加大向上 */
    }
    .mes .mes_timer {
        bottom: 30px !important;
        left: calc(50% - 123px) !important; 
    }
    .mes .tokenCounterDisplay {
        left: calc(50% - 140px) !important; 
        bottom: 10px !important;
    }

    /* 隐藏手机端滚动条 */
    :root {
        --scrollbar-width: 0px !important;
    }
    * {
        scrollbar-width: none !important;
    }
}

/* 14. 扩展兼容性修复 */
#text-display-content {
    color: #FFFFFF !important; 
}

#text-display-content::placeholder {
    color: rgba(255, 255, 255, 0.7) !important; 
}

/* 未选中样式 */
.quick-send-settings-checkbox {
    background-color: rgba(255, 255, 255, 0.2) !important; 
    border-color: rgba(255, 255, 255, 0.5) !important; 
}

/* 选中时样式 */
.quick-send-settings-checkbox:checked::before {
    box-shadow: inset 1em 1em #FFFFFF !important; 
}
