custom_css:
@import url("https://fontsapi.zeoseven.com/383/main/result.css");

body {
    font-family: "MaoKenTangYuan (beta)";
    font-weight: normal;
}

/* 底栏QR按键隐藏 */
#send_form:hover #qr--bar {
    height: 20px;
    transition: all 0.5s ease;
}

#qr--bar {
    height: 0.2px;
}

#chat .mes_block {
    /* 原有样式保持不变 */
    position: relative; /* 确保伪元素定位基准 */
}

/* 简约文字装饰系统 - 修改版 */
.mesIDDisplay,
.mes_timer,
.tokenCounterDisplay {
  position: relative;
  color: #000000 !important;
  font-weight: 600;
  letter-spacing: 0.05em;
  padding: 0.2em 0.5em;
  display: inline-block;
  border-radius: 8px;

  /* 双层文字阴影（保留原效果） */
  text-shadow: 
    0 1px 1px rgba(0, 0, 0, 1),
    0 2px 3px rgba(255, 255, 255, 1) !important;

  /* 修改边框颜色为浅紫色 */
  border: 1px solid #b8a9d9 !important;

  /* 静态投影（保留原透明度） */
  box-shadow: 0 2px 4px rgba(47, 89, 195, 0.08);
}

q {
    color: #000000;
    text-decoration: none !important;
    padding: 2px 8px;
    /* 修改为浅紫色渐变 */
    background: linear-gradient(90deg, 
        rgba(255, 255, 255, 0.98),
        #e8e0f8 40%,  /* 主渐变色 */
        rgba(248, 240, 255, 0.8) 100%
    ) !important;
    backdrop-filter: blur(6px);
    -webkit-backdrop-filter: blur(6px);
    border: 1px solid #b8a9d9 !important; /* 同步修改边框色 */
    border-radius: 8px;
    position: relative;
}
/* 引用标记 */
.mes_text blockquote::after {
    content: "“   ”";
    position: absolute;
    left: -15px;
    top: 5px;
    color: #666;
    font-size: 48px;
    font-family: serif;
    letter-spacing: 22px;
    opacity: 0.3;
}

/* 斜体文本 - 洛之对话框*/
em:not([class*="fa"]):not([class*="icon"]) {
    font-style: normal !important;
    display: inline-block;
    padding: 4px 32px 6px 32px; /* 左右留出图片空间 */
    background: linear-gradient(90deg, 
        rgba(220, 240, 255, 0.4), 
        rgba(220, 245, 245, 0.25)
    );
    backdrop-filter: blur(5px);
    -webkit-backdrop-filter: blur(5px);
    border-radius: 8px;
    position: relative;
    margin: 0 6px;
    box-shadow: 0 2px 8px rgba(195, 177, 225, 0.4);
}

em:not([class*="fa"]):not([class*="icon"])::before,
em:not([class*="fa"]):not([class*="icon"])::after {
    content: '';
    position: absolute;
    top: 50%;
    width: 24px;
    height: 24px;
    background: url('https://img.51shazhu.com/autoupload/3BW2tW9sbxpWqKI5VWce59iO_OyvX7mIgxFBfDMDErs/20250727/TcDn/804X1024/IMG_20250727_144833.png') no-repeat center/contain;
    transform: translateY(-50%);
}

em:not([class*="fa"]):not([class*="icon"])::before {
    left: 4px;
}

em:not([class*="fa"]):not([class*="icon"])::after {
    right: 4px;
    transform: translateY(-50%) scaleX(-1); /* 水平翻转图片 */
}

/* 移动端适配 */
@media (max-width: 768px) {
    q::before, q::after {
        display: none;
    }
}

/* 移动端适配 */
@media (max-width: 768px) {
  .inline-drawer-header {
    padding-right: 10px !important;

    &::before {
      right: 40px;
      font-size: 1em;
    }
  }
}

/*导航栏*/
#top-settings-holder {
    background: linear-gradient(145deg, 
        rgba(240, 237, 255, 0.92),  /* 主色调 */
        rgba(230, 225, 250, 0.95)   /* 微加深 */
    ) !important;
    backdrop-filter: blur(6px);
    border: 1px solid rgba(245, 242, 255, 0.98);
    box-shadow: 
        0 2px 8px rgba(210, 205, 230, 0.2),
        inset 0 1px 12px rgba(255, 255, 255, 0.6);
    border-radius: 2px;
}

