/* === ★以下代码请斟酌改动★ === */
select {
  background-position: right center; /* 图标位置 */
  background-repeat: no-repeat;
  background-color: rgba(255, 255, 255, 0.5);
  border: 1px solid var(--SmartThemeBodyColor);
  outline: none;
  box-sizing: border-box;
}

.mes_text blockquote {
  /* 核心布局 */
  position: relative;
  padding: 22px 20px 50px; /* 给胶带和底部图腾留空间 */
  margin: 1.2em 0.2em;
  background: #fff;

  /* 拍立得风格的立体阴影 */
  box-shadow:
    0 4px 6px rgba(0, 0, 0, 0.15),  /* 主阴影 */
    0 10px 15px rgba(0, 0, 0, 0.05); /* 拉远阴影 */

  /* 内部灰色边框（上左右4px、下8px） */
  border: none;
  border-radius: 0;
  box-sizing: border-box;

  /* 使用伪元素制造内边框 */
}

.mes_text blockquote::before {
  content: "";
  position: absolute;
  top: 6px;
  left: 6px;
  right: 6px;
  bottom: 55px;
  border: 1px solid rgba(232, 245, 255, 0.5); /* 浅紫边框 */
  pointer-events: none; /* 不影响交互 */
  z-index: 0;
}

/* 顶部中间半透明胶带 */
.mes_text blockquote::after {
  content: "";
  position: absolute;
  top: -12px;
  left: 50%;
  transform: translateX(-50%);
  width: 70px;
  height: 28px;
  background: var(--SmartThemeBodyColor);
  border-radius: 2px;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
  opacity: 0.5;
  z-index: 2;
}

/* 极简直线滑轨样式 */
input[type="range"] {
  -webkit-appearance: none;
  width: 100%;
  background: transparent;
  position: relative;
  margin: 20px 0; /* 垂直间距 */
}

/* 滑轨样式（浅紫色版本） */
input[type="range"]::-webkit-slider-runnable-track {
  height: 4px;
  background: #b8a9d990;  /* 替换为浅紫色 */
  border-radius: 10;
  box-shadow: none;
}


input[type="range"]::-moz-range-track {
  height: 5px;
  background: var(--SmartThemeBodyColor);
  border: 0;
}

/* 聚焦状态滑轨阴影 */
input[type="range"]:focus {
  outline: none;
}

input[type="range"]:focus::-webkit-slider-runnable-track {
  box-shadow: 0 0 0 2px rgba(255, 255, 255, 0.1);
}

/* 滑块样式 */
input[type="range"]::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 50px !important;
  height: 50px !important;
  border: 0 solid transparent !important;
  border-radius: 50% !important;
  box-shadow: 0 0px 0px rgba(232, 224, 245, 20);
  transition: all 0.2s ease;
  position: relative;
  top: 50% !important;
  transform: translateY(-50%);
  margin-bottom: -6px !important;
  cursor: pointer;
}

/* 分割线 */
hr {
    border: none;
