/*——————复选——————*/
/* 黑白哥特夜间风格 */
body {
  background: #121212; /* 深黑背景 */
}

input[type='checkbox'] {
  appearance: none;
  width: 20px;
  height: 20px;
  border: 2px solid #5a5a5a; /* 深灰色边框 */
  border-radius: 5px;
  cursor: pointer;
  position: relative;
  background: #1e1e1e; /* 暗灰色背景 */
  transition: all 0.3s cubic-bezier(0.68, -0.55, 0.27, 1.55);
  box-shadow: 
    inset 0 0 9px rgba(0, 0, 0, 0.5), /* 深色内阴影 */
    0 2px 8px rgba(0, 0, 0, 0.3);
}

input[type='checkbox']:hover {
  transform: scale(1.1);
  box-shadow: 
    inset 0 0 10px rgba(255, 255, 255, 0.1),
    0 0 15px rgba(255, 255, 255, 0.05);
  border-color: #8a8a8a; /* 浅灰色悬停边框 */
}

input[type='checkbox']:checked {
  background: #2a2a2a; /* 深灰色背景 */
  border-color: #b0b0b0; /* 浅灰色边框 */
  animation: jelly 0.6s ease;
  box-shadow: 
    inset 0 0 10px rgba(0, 0, 0, 0.7),
    0 2px 12px rgba(255, 255, 255, 0.1);
}

input[type='checkbox']:checked::after {
  content: '✚'; /* 保留复古十字标记 */
  position: absolute;
  top: 51%;
  left: 47%;
  transform: translate(-50%, -55%) rotate(0deg);
  color: #e0e0e0; /* 浅灰色标记 */
  font-size: 18px;
  font-weight: bold;
  text-shadow: 0 1px 4px rgba(0, 0, 0, 0.5);
}

#waifuMode:checked {
  background: #333; /* 更深的灰色背景 */
  border-color: #c0c0c0; /* 银灰色边框 */
  box-shadow: 
    inset 0 0 12px rgba(0, 0, 0, 0.8),
    0 0 22px rgba(255, 255, 255, 0.1);
}


