/*——————框美化三——————*/
textarea {
  background-color: #1a1a1a;        /* 深灰背景 */
  color: #e0e0e0;                  /* 浅灰文本 */
  border: 2px solid #333333;       /* 深灰边框 */

  width: 100%;
  display: block;
  box-sizing: border-box;
  border-radius: 9px;
  padding: 7px 15px;
  font-family: 'Inter', 'system-ui', sans-serif;
  font-size: 0.8rem;
  line-height: 1.6;
  resize: vertical;
  max-height: 60vh;
  transition: all 0.2s ease;

  box-shadow: 
    2px 2px 2px rgba(0, 0, 0, 0.5), /* 深色实阴影 */
    inset -2px -2px 9px rgba(0, 0, 0, 0.7); /* 深色内槽阴影 */

  &:focus {
    border-color: #444444;         /* 中灰边框 */
    outline: none;
    box-shadow: 
      2px 2px 4px rgba(0, 0, 0, 0.7), 
      inset -2px -2px 10px rgba(0, 0, 0, 0.8);
  }

  &::placeholder {
    color: #666666;               /* 中灰占位符 */
  }

  &::-webkit-scrollbar {
    width: 6px;
  }

  &::-webkit-scrollbar-track {
    background: #222222;
  }

  &::-webkit-scrollbar-thumb {
    background-color: #444444;
    border-radius: 1px;
  }
}

