/*——————框美化四——————*/
.drawer-content select {
  width: 100%;
  padding: px 6px 14px 20px;
  appearance: none;
  -webkit-appearance: none;
  -moz-appearance: none;
  cursor: pointer;

  /* 黑白哥特夜间主题 */
  background-color: rgba(20, 20, 20, 0.95);
  border: 2px solid rgba(80, 80, 80, 0.8);
  border-radius: px;

  /* 调整阴影方向 */
  box-shadow: 
    inset 4px 2px 3px rgba(0, 0, 0, 0.5), 
    3px 3px 3px rgba(10, 10, 10, 0.8);

  background-image: url("https://i.postimg.cc/05Tr2Cz2/Image-4888694645181541.png");
  background-repeat: no-repeat;
  background-position: right -10px center;
  background-size: 100px;

  /* 银灰文字 */
  font-family: 'Quicksand', sans-serif;
  font-size: 12px;
  color: #d0d0d0;
  letter-spacing: 0.5px;

  transition: all 0.1s cubic-bezier(0.175, 0.25, 0.32, 0.275);
  transform-origin: center;
}

.drawer-content select:hover {
  transform: scale(1.02);
  border-color: rgba(120, 120, 120, 0.9);
  background-color: rgba(30, 30, 30, 0.98);

  box-shadow: 
    inset 1px 1px 4px rgba(0, 0, 0, 0.7),
    4px 4px 12px rgba(0, 0, 0, 0.9);
}

.drawer-content select:focus {
  outline: none;
  border-color: rgba(160, 160, 160, 0.9);
  background-color: rgba(10, 10, 10, 1);

  box-shadow: 
    inset 1px 1px 5px rgba(0, 0, 0, 0.8),
    0 0 0 3px rgba(80, 80, 80, 0.5),
    5px 5px 16px rgba(0, 0, 0, 0.95);

  animation: jellyFocus 0.9s ease;
}

.drawer-content select option {
  background: rgba(25, 25, 25, 0.98);
  color: #c0c0c0;
  padding: 2px 20px;
  margin: 4px 0;
  border-radius: 12px;
  transition: all 0.3s ease;
}

.drawer-content select option:hover {
  background: rgba(60, 60, 60, 0.9) !important;
  transform: translateX(8px);
  box-shadow: 2px 2px 6px rgba(0, 0, 0, 0.5);
}

@keyframes jellyFocus {
  0% { transform: scale(5); }
  10% { transform: scale(1.05); }
  20% { transform: scale(0.98); }
  30% { transform: scale(1); }
}

/*——————框美化五——————*/
/* 黑白哥特夜间风格输入框 */
input:not([type="file" i], [type="image" i], [type="checkbox" i], [type="radio" i]) {
  background: #1a1a1a;
  border: 0px solid #333333;
  border-radius: 0rem;
  padding: 0.1rem 0.8rem;
  transition: all 0.25s ease;
  color: #e0e0e0;
  font-size: 0.6rem;
  height: 1.5rem;
  box-shadow: 
    8px 4px 2px rgba(0, 0, 0, 0.7),
    inset -2px -3px 4px rgba(40, 40, 40, 0.6);
}

/* 聚焦状态-哥特高亮 */
input:not([type="file" i], [type="image" i], [type="checkbox" i], [type="radio" i]):focus {
  outline: none;
  border-color: #666666;
  transform: translateY(-1px);
  box-shadow: 
    2px 2px 4px rgba(0, 0, 0, 0.8),
    inset -2px -2px 3px rgba(70, 70, 70, 0.7);
}

/* 悬停状态-深灰效果 */
input:not([type="file" i], [type="image" i], [type="checkbox" i], [type="radio" i]):hover {
  background: #222222;
  border-color: #444444;
}

/* 禁用状态-暗灰效果 */
input:not([type="file" i], [type="image" i], [type="checkbox" i], [type="radio" i]):disabled {
  background: #2a2a2a;
  border-color: #3a3a3a;
  color: #777777;
  cursor: not-allowed;
}
