/*——————文本编辑框美化——————*/
.reasoning_edit_textarea, .edit_textarea {
    padding: 15px 15px;
    margin: 0;
    width: 100%;
    max-height: 75vh;
    max-height: 75dvh;
    outline: none;

    background: #0d0d0d;
    border: 2px solid #2a2a2a;
    border-radius: 8px;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,0.8),
                3px 3px 6px rgba(0,0,0,0.5);

    color: #e0e0e0;
    font-size: 14px;
    line-height: 1.6;
    font-family: 'Helvetica Neue', sans-serif;

    transition: border-color 0.3s ease, 
                box-shadow 0.3s ease;

    &:focus {
        border-color: #555;
        box-shadow: inset 1px 1px 4px rgba(0,0,0,0.9),
                    3px 3px 8px rgba(0,0,0,0.7),
                    0 0 0 2px rgba(90, 90, 90, 0.5);
    }

    &::placeholder {
        color: #5a5a5a;
        opacity: 0.8;
    }

    scrollbar-width: thin;
    scrollbar-color: #444 #1a1a1a;

    &::-webkit-scrollbar {
        width: 1px;
        background: #1a1a1a;
    }

    &::-webkit-scrollbar-thumb {
        background: #444;
        border-radius: 1px;
    }
}

