  0 0 0 1px rgba(255, 255, 255, 0.9),          /* Á¨¨‰∏ÄÂ±ÇÊèèËæπ: ‰∫ÆÁôΩÈ´òÂÖâ */
  0 0 0 2px rgba(255, 172, 173, 0.2),          /* Á¨¨‰∫åÂ±ÇÊèèËæπ: Á≤âÁ∫¢Ëâ≤ÊüîËæπ */
