  box-shadow: 
    inset 2px 2px 4px rgba(255, 172, 173, 0.15), /* Ê∑°Á≤âËâ≤ÂÜÖÈò¥ÂΩ± #ffacad */
    inset -2px -2px 4px rgba(198, 217, 163, 0.2), /* Ê∑°ÁªøËâ≤ÂÜÖÈò¥ÂΩ± #c6d9a3 */
