input[type='checkbox'] {
  appearance: none;
  width: 24px;
  height: 24px;
  border: 3.5px solid #D8C9C6;  /* ÊµÖËóïÁ≤âËâ≤ÊèèËæπ - ÂÆûÁé∞ÊüîÂíåÁöÑÊµÖËâ≤ÊèèËæπ */
  border-radius: 20px;
  cursor: pointer;
  position: relative;
  background: rgba(255, 255, 255, 0.75);  /* È´òÈÄèÊòéÁôΩËâ≤ËÉåÊôØ - Â¢ûÂº∫ËΩªÁõàÊÑü */
  transition: all 0.3s cubic-bezier(0.68, -0.55, 0.27, 1.55);
  box-shadow: 
    inset 0 4px 3px rgba(224, 213, 213, 0.8),  /* ÊµÖÁÅ∞Á≤âÂÜÖÈô∑Èò¥ÂΩ± - Â¢ûÂä†Á´ã‰ΩìÊÑü */
    0 2px 6px rgba(215, 220, 210, 0.3);  /* Á≤âÁªøËâ≤Â§ñÈò¥ÂΩ± */
  vertical-align: middle;
}

input[type='checkbox']:hover {
  transform: scale(1.08);
  box-shadow: 
    inset 0 4px 3px rgba(180, 190, 175, 0.9),  /* ÊµÖËãîËóìÁªøÂÜÖÈò¥ÂΩ± */
    0 0 12px rgba(200, 220, 210, 0.3);  /* ÊüîÂÖâÁªøÊôïÊüìÊïàÊûú */
  border-color: #C1D4C8;  /* Á≤âÁªøËâ≤ÊÇ¨ÂÅúÊèèËæπ */
}

input[type='checkbox']:checked {
  background: #E1EFE7;  /* ËñÑËç∑ÁªøÈÄâ‰∏≠ËÉåÊôØ */
  border-color: #F1E9E9;  /* Â´©ÁªøÈÄâ‰∏≠ËæπÊ°Ü */
  animation: jelly 0.6s ease;
  box-shadow: 
    inset 0 4px 5px rgba(180, 200, 190, 0.9),  /* ÊüîÁªøÂÜÖÈô∑Èò¥ÂΩ± */
    0 2px 8px rgba(200, 220, 210, 0.3);  /* Êâ©Êï£ÂÖâÊôï */
}

/* ÈÄâ‰∏≠Áä∂ÊÄÅÂõæÊ†á */
input[type='checkbox']:checked::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 120%;
  height: 100%;
  background-image: url('https://i.postimg.cc/xdwWFBwD/Image-1516468205267926.png');
  background-size: 140%;
  background-repeat: no-repeat;
  background-position: center;
  transform: translate(-47%, -42%);
  filter: brightness(1.1) saturate(0.9);  /* ÂõæÊ†á‰∫ÆÂ∫¶Â¢ûÂº∫ */
  border-radius: 20px;
}
/*‚Äî‚Äî‚Äî‚Äî‚Äî‚ÄîÊñáÂ≠óÊïàÊûúÁæéÂåñ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî*/
/* ‰∏ªÂÆπÂô® - ÁôΩÊó•Ê¢¶‰∏ªÈ¢òÂü∫Â∫ï */
background: #FFFFFF !important;  /* Á∫ØÁôΩÂü∫Â∫ïÔºåÁ¨¶ÂêàÊó•Èó¥Ê®°Âºè */
box-shadow: 
  0 0 0 2px #F0F9D2,            /* ÊµÖÁªøÊèèËæπ1ÔºöÊüîÂíåÁöÑÊ∑°ÁªøËâ≤ËΩÆÂªì */
  0 0 0 4px #FFE6F0,             /* Á≤âÁ∫¢ÊèèËæπ2ÔºöÊµÖÁ≤âËâ≤‰∏≠Èó¥Â±Ç */
  0 0 0 6px #D8F0C1,             /* ÁªøÊèèËæπ3ÔºöÊõ¥È•±ÂíåÁöÑÊ∑°ÁªøÂ§ñÊ°Ü */
  4px 4px 8px rgba(180,200,170,0.25), /* ÊµÖÁªøÈò¥ÂΩ±ÔºöÊüîÂíåÁöÑËá™ÁÑ∂ÂÖâÊäïÂΩ± */
  inset 2px 2px 4px rgba(220,230,210,0.6), /* ÊµÖÁªøÂÜÖÈò¥ÂΩ±ÔºöÂ∑¶‰∏äÂæÆÂÖâ */
  inset -2px -2px 4px #FFFFFF;   /* Á∫ØÁôΩÂÜÖÈò¥ÂΩ±ÔºöÂè≥‰∏ãÂèçÂÖâÊïàÊûú */

