#chat {
  /* ä¿ç•™åŽŸæœ‰æ ·å¼ */
  max-height: calc(100vh - calc(var(--topBarBlockSize) + var(--bottomFormBlockSize)));
  overflow-x: hidden;
  padding-bottom: 0;
  overflow-y: scroll;
  display: flex;
  bottom: 10px;
  backdrop-filter: blur(var(--SmartThemeBlurStrength));
  background-color: var(--SmartThemeChatTintColor);
  -webkit-backdrop-filter: blur(var(--SmartThemeBlurStrength));
  text-shadow: 0px 0px calc(var(--shadowWidth) * 1px) var(--SmartThemeShadowColor);
  flex-direction: column;
  z-index: 30;
  flex-grow: 1;

  /* é¡¶éƒ¨æ¸å˜é®ç½© */
  --top-fade-height: 7px; /* é¡¶éƒ¨æ¸å˜åŒºåŸŸé«˜åº¦ */
  --top-fade-start: 0px;   /* é¡¶éƒ¨å¼€å§‹æ¸å˜çš„ä½ç½® */

  /* åªä¿ç•™é¡¶éƒ¨é®ç½© */
  mask-image: linear-gradient(
    to bottom, 
    rgba(0,0,0,0) var(--top-fade-start), 
    rgba(0,0,0,1) calc(var(--top-fade-start) + var(--top-fade-height)),
    rgba(0,0,0,1) 100% /* ç¡®ä¿æ¸å˜åŽä¿æŒä¸é€æ˜Ž */
  );

  -webkit-mask-image: linear-gradient(
    to bottom, 
    rgba(0,0,0,0) var(--top-fade-start), 
    rgba(0,0,0,1) calc(var(--top-fade-start) + var(--top-fade-height)),
    rgba(0,0,0,1) 100%
  );

  mask-size: 100% 100%;
  -webkit-mask-size: 100% 100%;
  /* ç§»é™¤ç»„åˆå±žæ€§ */
}
