  width: 300px;
  margin-left: auto;
  margin-right: auto;
}

/* ÂÖ®Â±ÄÁõíÊ®°ÂûãËÆæÁΩÆ */
#toast-container * {
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
}

/* Ê†áÈ¢òÂ≠ó‰Ωì */
.toast-title {
  font-weight: 700;
}

/* ÂºπÁ™óÂü∫Á°ÄÊ†∑Âºè */
.toast {
  position: relative;
  padding: 15px 20px 15px 65px; 
  min-height: 60px;
  background: rgba(248,248,248,0.85); 
  border-radius: 8px;
}

/* ÂõæÁâáÂÆπÂô® */
.toast::before {
  content: "";
  position: absolute;
  left: 6px;
  top: 50%;
  transform: translateY(-50%);
  width: 36px;
  height: 36px;
  background: 
    url("https://i.postimg.cc/g2Xk6WfH/Image-926875252929630.png")
    center/contain no-repeat;
}

/* ÊàêÂäüÂºπÁ™óÂõæÁâá */
