  transition: all 0.25s ease;
  color: #4a3f35;
  font-size: 0.6rem;
  height: 1.2rem;
}

/* 聚焦状态-复古高亮 */
input:not([type="file" i], [type="image" i], [type="checkbox" i], [type="radio" i]):focus {
  outline: none;
  border-color: #a8957e;
  transform: translateY(-1px);
}

/* 悬停状态-浅褐效果 */
input:not([type="file" i], [type="image" i], [type="checkbox" i], [type="radio" i]):hover {
  background: #f1e8d6;
  border-color: #8c7a63;
}

/* 禁用状态-灰褐效果 */
input:not([type="file" i], [type="image" i], [type="checkbox" i], [type="radio" i]):disabled {
  background: #e6ded0;
  border-color: #b8ab93;
  color: #9d8e7a;
