.reasoning_edit_textarea, .edit_textarea {
    /* 基础布局 */
    padding: 15px 15px;
    margin: 0;
    width: 100%;
    max-height: 75vh;
    max-height: 75dvh;
    outline: none;

    /* 复古油画风格 */
    background: #faf7f0; /* 奶油色背景 */
    border: 2px solid #d7c9a8; /* 浅金色边框 */
    border-radius: 8px;
    box-shadow: inset 0 1px 3px rgba(183, 165, 133, 0.1); /* 内阴影增加深度感 */

    /* 文字系统 */
    color: #5a4d3d; /* 深褐色文字 */
    font-size: 16px;
    line-height: 1.6;
    font-family: 'Helvetica Neue', sans-serif;

    /* 过渡动画 */
    transition: border-color 0.3s ease, 
                box-shadow 0.3s ease;

    /* 聚焦状态 - 复古风格 */
    &:focus {
        border-color: #b8a58a; /* 深褐色边框 */
        box-shadow: inset 0 1px 3px rgba(183, 165, 133, 0.2), 
                    0 0 0 2px rgba(183, 165, 133, 0.15); /* 双层阴影效果 */
    }

    /* 占位符颜色 */
    &::placeholder {
        color: #a99e8b; /* 浅褐色占位符 */
        opacity: 0.8;
    }

    /* 滚动条美化 */
    scrollbar-width: thin;
    scrollbar-color: #c9b89b #f1ece0; /* 浅褐色滑块/米色轨道 */

    &::-webkit-scrollbar {
        width: 1px;
        background: #f1ece0; /* 浅米色轨道 */
    }

    &::-webkit-scrollbar-thumb {
        background: #c9b89b; /* 浅褐色滑块 */
        border-radius: 1px;
    }
}

