  /* åªä¿ç•™é¡¶éƒ¨é®ç½© */
  mask-image: linear-gradient(
    to bottom, 
    rgba(0,0,0,0) var(--top-fade-start), 
    rgba(0,0,0,1) calc(var(--top-fade-start) + var(--top-fade-height)),
    rgba(0,0,0,1) 100% /* ç¡®ä¿æ¸å˜åä¿æŒä¸é€æ˜ */
  );

  -webkit-mask-image: linear-gradient(
    to bottom, 
    rgba(0,0,0,0) var(--top-fade-start), 
    rgba(0,0,0,1) calc(var(--top-fade-start) + var(--top-fade-height)),
    rgba(0,0,0,1) 100%
  );

  mask-size: 100% 100%;
  -webkit-mask-size: 100% 100%;
  /* ç§»é™¤ç»„åˆå±æ€§ */
}
