}

/* --- å¤é€‰æ¡† (Checkboxes) - å¤œé—´é€‚é… --- */
input[type='checkbox'] {
    appearance: none;
    -webkit-appearance: none;
    -moz-appearance: none;
    width: 18px;
    height: 18px;
    background-color: var(--checkbox-unchecked-bg);
    border: 2px solid var(--checkbox-unchecked-border);
    border-radius: 4px;
    cursor: pointer;
    position: relative;
    vertical-align: middle;
    margin-right: 8px;
    transition: background-color 0.2s, border-color 0.2s;
}

input[type='checkbox']:checked {
    background-color: var(--checkbox-checked-bg);
    border-color: var(--checkbox-checked-bg);
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='%23e0e0e0'%3e%3cpath d='M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z'/%3e%3c/svg%3e");
    background-size: 1%;
    background-position: center;
    background-repeat: no-repeat;
}

