custom_css:
/* 朝雾·古风浅粉蓝 修正版
   配色：纯浅粉+浅蓝色系
   基于朝雾主题二改
   原创作者咩野(DC@lambkinling)
   请勿二传、商用 */

/* ========== 字体设置 ========== */
@import url("https://fontsapi.zeoseven.com/2/main/result.css");
@import url("https://fontsapi.zeoseven.com/467/main/result.css");

body {
    font-family: "LXGW ZhenKai GB";
    font-weight: normal;
}

/* ========== 变量控制 ========== */
:root {
  --混合: color-mix(in srgb, var(--SmartThemeBorderColor) 60%, #fff);
  --对话背景色: color-mix(in srgb, var(--SmartThemeUnderlineColor) 25%, transparent);

  --标题栏-1: color-mix(in srgb, var(--SmartThemeBorderColor) 50%, transparent);
  --标题栏-2: color-mix(in srgb, var(--SmartThemeBorderColor) 70%, transparent);
  --标题栏-3: color-mix(in srgb, var(--SmartThemeBorderColor) 30%, #fff);

  --小圆角: 6px;
  --中圆角: 8px;

  --内阴影: rgba(255, 255, 255, 0.85);
  --发光: rgba(255, 240, 245, 0.9);
  --外阴影: rgba(150, 130, 170, 0.1);
  --外阴影-浅: rgba(150, 130, 170, 0.05);
}

/* ========== 自定义文案变量 ========== */
:root {
  --底栏输入框: "执笔欲书，落墨成思";
  --底部文字大小: calc(var(--mainFontSize) * 0.75);
  --User消息底部文字: "言尽于此，后会有期";
  --Char消息底部文字: "静候君至，再叙佳话";
  --思维链: "· 心绪流转 ·";
  --名字大小: 0.85rem;
  --名字前缀: "君/";
  --书名大小: 1.15rem;
  --User书名: "《青衫录》";
  --Char书名: "《云水谣》";
  --文案字体大小: 0.6rem;

  --User文案: "山中一夜雨，树杪百重泉。";
  --Char文案: "雨打青松山更幽，泉穿白石水自流。";

  --User顶部图片/* 图源pinterest */: url("https://files.catbox.moe/vgkskp.gif");
  --Char顶部图片: url("https://files.catbox.moe/vgkskp.gif");
  --菜单页头像框: url("https://files.catbox.moe/6yta6e.png");

  --加载更多消息: "流水落花春去也，天上人间。\A- 故事未完待续 -";
}

/* ========== 字体设置 ========== */
/* 书名、名字、文案统一使用南宋古籍体 */
.mes .name_text,
.mes .name_text::before,
.mes .name_text::after,
.mes[is_user="false"] .name_text,
.mes[is_user="false"] .name_text::before,
.mes[is_user="false"] .name_text::after {
    font-family: "NanoOldSong-A", serif !important;
    font-weight: normal !important;
    letter-spacing: 0.1em !important;
}

.mes .name_text::after {
    font-size: calc(var(--书名大小) + 0.5px) !important;
}

.mes .name_text {
    font-size: calc(var(--名字大小) + 0.5px) !important;
}

.mes .name_text::before {
    font-size: calc(var(--名字大小) + 0.5px) !important;
}

/* ========== 文案位置调整 ========== */
/* User文案向右移动20px */
.mes[is_user="true"] .avatar::after {
    font-family: "NanoOldSong-A", serif !important;
    font-weight: normal !important;
    margin-left: 20px !important;
    letter-spacing: 0.05em !important;
}

/* Char文案向右移动20px */
.mes[is_user="false"] .avatar::after {
    font-family: "NanoOldSong-A", serif !important;
    font-weight: normal !important;
    margin-left: 20px !important;
    letter-spacing: 0.05em !important;
}

/* ========== 菜单界面背景改为纯色 ========== */
body .drawer-content {
  background: var(--SmartThemeBlurTintColor) !important;
  background-image: none !important;
}

#select_chat_popup,
.popup {
  background: var(--SmartThemeBlurTintColor) !important;
  background-image: none !important;
}


/* 文本样式 */
q {
  position: relative;
  background-image: linear-gradient(to right,
    transparent 0%, var(--对话背景色) 20px, var(--对话背景色) calc(100% - 20px), transparent 100%);
  background-size: 100% 50%;
  border-radius: 1px;
  box-shadow: 0.8px 0.8px 1px var(--外阴影-浅);
}

em {
  color: #fff !important;
  text-shadow: 0 0 1px var(--SmartThemeEmColor), 0 0 2.5px var(--SmartThemeEmColor);
}

del {
  color: var(--SmartThemeEmColor);
  text-decoration-color: crimson;
}

code {
  color: #fff !important;
  background: var(--SmartThemeUnderlineColor) !important;
  border: none;
  box-shadow: 1px 1px 1px var(--SmartThemeUnderlineColor) !important;
}

.hljs,
.mes pre code {
  position: relative;
  border-radius: 3px;
  color: var(--SmartThemeBodyColor) !important;
  font-size: var(--mainFontSize) !important;
  line-height: 1.6;
  border: none;
  box-shadow: inset 0 0 12px var(--内阴影), 1px 1px 2px var(--外阴影) !important;
  background-color: rgba(250, 245, 255, 0.95) !important;
  background-image: none !important;
}

blockquote {
  background: linear-gradient(to right, #fff, var(--对话背景色), transparent) !important;
  border-color: var(--SmartThemeEmColor) !important;
}

hr {
  background: color-mix(in srgb, var(--SmartThemeBorderColor) 75%, transparent);
}

/* ========== 部分通用 ========== */
.avatar img,
body.big-avatar .avatar img {
  box-shadow: none;
  border: none;
}

.avatar:not(.mes .avatar):not(#HotSwapWrapper .avatar) {
  position: relative;
}

.avatar:not(.mes .avatar):not(#HotSwapWrapper .avatar)::before {
  content: "";
  position: absolute;
  aspect-ratio: 1 / 1;
  width: 140%;
  top: 47%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: var(--菜单页头像框);
  background-size: 100%;
}

::-webkit-scrollbar {
  width: 4px;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(135deg, var(--SmartThemeBorderColor) 0%, var(--混合) 100%);
  border: 0.2px solid var(--SmartThemeBorderColor) !important;
  box-shadow: none !important;
}

#chat::-webkit-scrollbar {
    display: none;
}

input[type="range"] {
  background: linear-gradient(185deg, var(--SmartThemeBorderColor) 0%, var(--混合) 100%) !important;
  border-radius: 15px !important;
  height: 5px !important;
  filter: none !important;
  box-shadow: none !important;
}

input[type="range"]::-webkit-slider-thumb {
  filter: none !important;
  border: none !important;
  border-radius: 50%;
  background: linear-gradient(135deg, var(--SmartThemeBorderColor) 0%, var(--混合) 100%);
  box-shadow: -1px -1px 1px var(--内阴影), 1px 1px 1px var(--外阴影) !important;
}

#chat {
  background: none;
  border: none;
  max-height: calc(100dvh + var(--bottomFormBlockSize));
  height: calc(100dvh + var(--bottomFormBlockSize));
}

body:not(.waifuMode) #sheld {
  top: var(--bottomFormBlockSize);
  height: calc(100dvh - var(--bottomFormBlockSize));
  min-height: calc(100dvh - var(--bottomFormBlockSize));
}

/* ========== 顶栏 ========== */
#top-bar,
body.no-blur #top-bar {
  box-shadow: none !important;
  outline: 1px solid var(--SmartThemeBlurTintColor) !important;
}

.drawer-icon.fa-fw {
  display: flex;
  overflow: visible;
  justify-content: center;
  font-size: 1.2em;
}

.drawer-icon.fa-fw::before {
  color: var(--SmartThemeBorderColor);
}

#leftNavDrawerIcon::before { content: "\f0ae"; }
#API-status-top:not(.redOverlayGlow)::before { content: "\f0c6"; }
#API-status-top.redOverlayGlow::before { content: "\f714" !important; color: crimson !important; }
#advanced-formatting-button .drawer-icon::before { content: "\f1dc"; }
#WI-SP-button .drawer-icon::before { content: "\f02a"; }
#user-settings-button .drawer-icon::before { content: "\f49e"; }
#backgrounds-drawer-toggle .drawer-icon::before { content: "\f302"; }
#extensions-settings-button .drawer-icon::before { content: "\f1ea"; }
#persona-management-button .drawer-icon::before { content: "\f21e"; }
#rightNavDrawerIcon::before { content: "\f2b9"; }
#table_drawer_icon.drawer-icon::before { content: "\f080"; }

.drawer-icon.fa-fw:hover {
  filter: drop-shadow(0 0 1px var(--标题栏-1)) drop-shadow(0 0 3px var(--标题栏-1)) !important;
  transform: scale(1.15);
}

#extensionTopBar {
  border: none !important;
  border-bottom: 1px solid var(--混合) !important;
  border-radius: 0 0 var(--小圆角) var(--小圆角) !important;
}

/* ========== 底栏输入框 ========== */
#form_sheld {
  margin: 0 auto !important;
  position: absolute;
  bottom: 5px;
}

#form_sheld:has(#send_textarea:focus),
#form_sheld:has(#send_textarea:not(:placeholder-shown)) {
  position: static;
}

#send_form,
body.no-blur #send_form {
  width: 92%;
  border-radius: var(--中圆角);
  padding: 3px;
  border: 1.2px solid var(--SmartThemeBorderColor) !important;
  outline: 1.5px solid var(--混合) !important;
  box-shadow: inset 0 0 20px var(--内阴影), 0 0 10px var(--发光), 0 0 7px var(--发光);
}

#send_textarea {
  clip-path: none !important;
  border: none;
}

#send_form textarea:focus {
  box-shadow: inset 1px 1px 2px var(--混合), inset 0 0 5px var(--发光);
  background: color-mix(in srgb, var(--SmartThemeBorderColor) 15%, transparent);
}

#quick-reply-rocket-button,
#send_form .fa-solid {
  font-size: calc(var(--mainFontSize) * 1.25);
  color: var(--混合);
}

#options_button::before { content: "\f10d"; }
#extensionsMenuButton::before { content: "\f891"; }
#send_but::before { content: "\f10e"; }
#mes_stop i::before { content: "\f05e"; color: crimson; }

#send_textarea::placeholder { opacity: 0; }

#nonQRFormItems {
    position: relative;
    display: flex;
    align-items: center;
}

#nonQRFormItems::before {
    content: var(--底栏输入框);
    position: absolute;
    left: calc(50% + 10px);
    top: 50%;
    transform: translate(-50%, -50%);
    font-size: 0.7rem;
    color: var(--SmartThemeBorderColor);
    pointer-events: none;
    z-index: 1;
}

#nonQRFormItems:has(#send_textarea:focus)::before,
#nonQRFormItems:has(#send_textarea:not(:placeholder-shown))::before {
    display: none !important;
}

.list-group,
#quickPersonaImg,
.options-content,
#options {
  box-shadow: none !important;
}

.list-group-item .quickPersonaMenuImg {
    box-shadow: none;
    border: none;
}

#qr--bar > .qr--buttons .qr--button {
  border: none !important;
  border-radius: var(--小圆角) !important;
  background: transparent;
  box-shadow: 1px 1px 2px var(--混合), 0 0 3px var(--混合) !important;
}

/* ========== 聊天框 ========== */
.last_mes {
  margin-bottom: 60px !important;
}

.last_mes .mesAvatarWrapper {
  padding-bottom: 0px;
}

.last_mes[is_user="false"] .mes_block {
  margin-bottom: 20px !important;
}

.mes {
  flex-direction: column;
  position: relative;
  width: calc(100% - 15px);
  margin: 15px auto;
  padding: 12px 7px 17px;
  border-radius: var(--小圆角);
  background-color: var(--SmartThemeChatTintColor);
  box-shadow: 0 0 2px var(--发光), 0 0 5px var(--发光), 0 0 3px var(--发光);
}

.mes .timestamp {
  position: absolute;
  top: calc(100% - 26px);
  left: 50%;
  transform: translateX(-50%);
  padding: 2px 5px;
  color: var(--混合);
  background-color: var(--SmartThemeChatTintColor);
  opacity: 1 !important;
}

.last_mes[is_user="false"] .timestamp {
  top: calc(100% - 45px);
}

.mes .ch_name::before {
  content: var(--User消息底部文字);
  position: absolute;
  transform: translateX(-50%);
  left: 50%;
  font-size: var(--底部文字大小);
  color: var(--混合);
}

.mes[is_user="false"] .ch_name::before {
  content: var(--Char消息底部文字);
}

.mes_block {
  display: flex;
  flex-direction: column;
  overflow: visible;
  transform: none !important;
  padding: 0;
  border-top: 1px dashed var(--混合);
  border-bottom: 3.5px double var(--混合);
  background: url("https://files.catbox.moe/xswf7x.png");
  background-size: 100% auto;
  background-repeat: repeat-y;
}

.mes_text {
  padding: 0 2px;
  text-align: justify;
}

.mes[is_user="true"] .mes_text {
  padding-top: 7px;
}

.mes[is_user="true"] p:last-child {
  margin-bottom: 0;
}

.mes_text p:empty { display: none; }

.mes_text p { text-indent: 2em; }

.mes_button,
.mes_edit_buttons {
  font-size: calc(var(--mainFontSize) * 1.05);
  filter: none !important;
  color: var(--SmartThemeBorderColor);
}

.mes .menu_button {
  opacity: 1;
  transform: scale(0.85);
  border-radius: var(--中圆角);
  border: none;
  filter: none !important;
  box-shadow: 0 0 3px var(--发光), 1px 1px 3px var(--混合), inset 0 0 12px var(--内阴影) !important;
}

.mes .fa-star::before { content: "\f02b" !important; }
#chat .fa-solid.fa-star { color: gold !important; }
.fa-star::before { content: "\f004" !important; }
.fa-solid.fa-star { color: red !important; }
.mes_edit::before { content: "\f00c"; }
.extraMesButtonsHint::before { content: "\f00d" !important; }

.mes .ch_name {
  order: 1;
  align-items: center;
}

.ch_name:has(.extraMesButtonsHint[style*="opacity: 0"])::before {
  display: none !important;
}

.mes_reasoning_header {
  background-color: transparent;
  margin: 0 auto;
}

.mes_reasoning_arrow { display: none; }

.mes_reasoning_header_title {
  font-size: 0;
}

.mes_reasoning_header_title::before {
  content: var(--思维链);
  font-size: calc(var(--mainFontSize) * 0.9);
  background: var(--SmartThemeChatTintColor);
  color: var(--SmartThemeEmColor);
  border-radius: var(--小圆角);
  padding: 2px 6px;
  box-shadow: 0 0 1px var(--混合), 1px 1px 2.5px var(--混合), inset 0 0 6px var(--内阴影);
}

@media (max-width: 768px) {
  .mes_reasoning_details { position: relative; }
  .mes_reasoning_details[open] { width: 100%; }
  .mes_reasoning {
    width: 100%;
    max-height: 500px;
    overflow: hidden auto;
    font-size: calc(var(--mainFontSize) * 0.9);
    border-radius: var(--小圆角);
    border: 1px dashed var(--SmartThemeBorderColor);
    background: color-mix(in srgb, var(--SmartThemeBorderColor) 15%, #fff);
  }
  .mes_reasoning_actions {
     position: absolute;
     right: 2px;
     top: 2px;
     z-index: 10;
     flex-shrink: 0;
  }
}

.mes .name_text {
  position: absolute;
  top: 75px;
  left: 12%;
  writing-mode: vertical-rl;
  color: var(--SmartThemeQuoteColor);
  font-size: var(--名字大小);
}

.mes .name_text::before {
  content: var(--名字前缀);
}

.mes .name_text::after {
  content: var(--User书名);
  font-size: var(--书名大小);
  position: absolute;
  white-space: nowrap;
  top: -75px;
  right: 100%;
  margin-right: 5px;
}

.mes[is_user="false"] .name_text::after {
  content: var(--Char书名);
}

.mesAvatarWrapper {
  display: flex;
  align-self: flex-start;
  align-items: flex-end;
  justify-content: flex-end;
  width: 100%;
  height: 170px;
  flex: 0 0 auto;
  gap: 5px;
  margin-bottom: 2px;
  padding-bottom: 5px !important;
  border-bottom: 1.2px solid var(--混合);
}

.mesAvatarWrapper::before {
  content: "";
  position: absolute;
  top: 8px;
  right: 8px;
  width: 77%;
  height: 130px;
  background: var(--User顶部图片);
  background-repeat: no-repeat;
  background-size: 100% auto;
  background-position: 100% 70%;
  border-radius: var(--小圆角);
  box-shadow: -1px 2px 3px var(--外阴影);
}

.mes[is_user="false"] .mesAvatarWrapper::before {
  background: var(--Char顶部图片);
  background-repeat: no-repeat;
  background-size: 100% auto;
  background-position: 100% 50%;
}

.mes .avatar {
  position: relative;
  bottom: -8px;
}

.mes .avatar::after {
  content: var(--User文案);
  white-space: pre-wrap;
  font-size: var(--文案字体大小);
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  margin-left: -5px;
  color: var(--SmartThemeEmColor);
}

.mes[is_user="false"] .avatar {
  position: relative;
}

.mes[is_user="false"] .avatar::after {
  content: var(--Char文案);
}

.mes .avatar img {
  bottom: 5px;
  transform: scale(0.55);
  outline: 1px solid var(--混合);
  outline-offset: 2px;
}

.mes .mesIDDisplay:not(:empty),
.mes .mes_timer:not(:empty),
.mes .tokenCounterDisplay:not(:empty) {
  opacity: 0.75;
  font-size: 0.7em;
  color: var(--混合);
}

.mes .mes_timer:not(:empty)::before,
.mes .tokenCounterDisplay:not(:empty)::before {
  content: "/  ";
  margin-left: 1px;
  color: var(--SmartThemeEmColor);
}

.swipe_right,
.swipe_left {
  opacity: 0.75;
  color: var(--混合);
  font-size: 1.2em;
}

.swipe_right::before { content: "\f101"; }
.swipe_left::before { content: "\f100"; }

.swipeRightBlock {
  flex-direction: row-reverse;
  bottom: 10px;
  margin-right: 15px;
}

.swipe_left { transform: translateY(14px) !important; }
.swipes-counter { transform: translate(10px, 4px) !important; }

/* ========== 欢迎页 ========== */
.welcomePanel .recentChatList .recentChat {
    margin-bottom: 10px;
    border-radius: var(--小圆角);
    background: var(--SmartThemeBlurTintColor);
    outline: 1.5px solid var(--SmartThemeBlurTintColor) !important;
    box-shadow: 0 0 3px var(--发光), 0 0 7px var(--发光);
}

.chatNameContainer { padding: 1px 3.5px; }

.mes_button.showRecentChats .fa-circle-chevron-down::before { content: "\f0e0"; }
.mes_button.hideRecentChats .fa-circle-xmark::before { content: "\f658"; }
.mes_button.hideRecentChats,
.mes_button.showRecentChats { opacity: 0.75; }

/* ========== 菜单抽屉 ========== */
/* 背景已改为纯色，见上方修改 */

@media (max-width: 600px) {
  #left-nav-panel,
  #right-nav-panel {
    max-height: calc(100dvh - var(--topBarBlockSize));
    border-radius: 0 0 8px 8px;
    border-color: var(--SmartThemeBlurTintColor);
    border-bottom: 1px solid var(--SmartThemeBorderColor);
    padding-right: 0;
  }
}

#rm_button_characters { font-size: 1.2em; }

.reverse_proxy_warning {
    border-color: crimson !important;
    border-radius: var(--小圆角) !important;
    background-color: transparent !important;
}

.standoutHeader,
.inline-drawer-header,
#top-settings-holder h4,
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header {
  filter: none !important;
  padding: 5px 8px;
  margin-bottom: 5px;
  border-radius: 5px;
  border: 0.8px solid var(--SmartThemeBorderColor);
  background: radial-gradient(55% 105% ellipse at left top, var(--标题栏-2), transparent),
              radial-gradient(35% 55% ellipse at 60% 110%, var(--内阴影), transparent),
              radial-gradient(120px circle at right center, var(--标题栏-1), transparent),
              var(--标题栏-3);
  box-shadow: 0.8px 0.8px 0 0.5px var(--混合),
              inset 0 0 12px var(--内阴影),
              inset 0 0 4px var(--内阴影);
}

.inline-drawer-icon {
  color: var(--SmartThemeBorderColor);
  filter: drop-shadow(0 0 1px var(--发光)) drop-shadow(0 0 4px var(--发光)) !important;
  opacity: 0.75;
  font-size: 1em;
}

.inline-drawer-icon::before { content: "\f56b" !important; }
.inline-drawer-icon.up::before { content: "\f52d" !important; }

#openai_api > h4.margin0 { margin-bottom: 8px; }
#UI-presets-block { padding-top: 5px; gap: 2px; }

div[name="themeElements"] > div.inline-drawer.wide100p > div.inline-drawer-toggle.inline-drawer-header {
  margin-top: 10px;
}

.menu_button:not(.mes_edit_buttons *) {
  box-shadow: inset 0 0 1px var(--标题栏-1), 1px 1px 2px var(--混合), 0 0 5px var(--发光);
  color: var(--SmartThemeBorderColor);
  border: none;
  filter: none !important;
}

#bulkDeleteButton,
#delete_button,
#persona_delete_button,
#world_popup_delete,
.menu_button.popup-button-ok,
#delete_connection_profile,
#delete_oai_preset,
#dialogue_del_mes_ok,
.mes_edit_cancel.menu_button,
.mes_edit_buttons .mes_edit_delete {
  background: crimson !important;
  color: rgba(0, 0, 0, 1) !important;
}

input[type="checkbox"] {
  background: var(--SmartThemeBlurTintColor);
  border-radius: 3px;
  filter: none;
  border: none;
  outline: none;
  box-shadow: inset 0 0 3px var(--标题栏-3) !important;
}

.checkbox_label { display: flex; align-items: center; }

input[type="checkbox"]:checked {
  background: color-mix(in srgb, var(--SmartThemeBorderColor) 30%, #fff) !important;
  box-shadow: inset 0 0 4px var(--内阴影) !important;
}

input[type="checkbox"]::before {
  box-shadow: inset 1em 1em var(--标题栏-1);
}

select.text_pole,
select {
  outline: none !important;
  border: none;
  border-radius: var(--小圆角);
  box-shadow: 1px 1px 2px var(--混合), inset 0 0 15px var(--内阴影);
  background-color: color-mix(in srgb, var(--SmartThemeBorderColor) 25%, #fff);
  background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path fill="%23B6B6B6" d="M5.5 7l4.5 4.5 4.5-4.5 1.5 1.5-6 6-6-6z"/></svg>');
  background-position: right 2px center;
  background-repeat: no-repeat;
  background-size: 1.2em;
}

#tag_import_setting { margin-top: 5px; }

input:not([type]),
input[type="text"],
input[type="search"],
input[type="number"],
textarea.text_pole,
textarea.textarea_compact,
textarea:not(#send_textarea) {
  border: none;
  background: color-mix(in srgb, var(--SmartThemeBorderColor) 15%, #fff);
  box-shadow: 1px 1px 2.5px var(--混合), inset 0 0 15px var(--内阴影);
}

input:not([type]):hover,
input[type="text"]:hover,
input[type="search"]:hover,
input[type="number"]:hover,
textarea.text_pole:hover,
textarea.textarea_compact:hover,
textarea:not(#send_textarea):hover {
  outline: none;
  box-shadow: inset 1px 1px 1px var(--混合), inset 0 0 2px var(--混合);
  background: color-mix(in srgb, var(--SmartThemeBorderColor) 5%, #fff);
}

/* 弹窗背景已改为纯色，见上方修改 */

#dialogue_popup_text { padding-right: 5px; }
.popup-content:has(#showRawPrompt) { overflow-y: auto; }

div.toast.toast-success {
  background-color: var(--混合) !important;
  border-radius: var(--小圆角) !important;
  color: var(--SmartThemeBodyColor) !important;
  box-shadow: inset 0 0 20px var(--发光), 1px 1px 5px var(--发光), 0 0 6px var(--混合) !important;
}

.range-block > .toggle-description { margin-right: 0; }
.range-block-title { text-align: left; }

label[for="openai_image_inlining"] + div,
#openai_settings .inline-drawer-header .flex-container {
  gap: 0;
  line-height: 1.2;
}

#openai_settings .inline-drawer-header .flex-container b { margin-right: 2px; }

#completion_prompt_manager_list {
  border: none;
  background: color-mix(in srgb, var(--SmartThemeBorderColor) 20%, #fff);
  box-shadow: 1px 1px 2.5px var(--混合), inset 0 0 30px var(--内阴影);
  padding: 0 8px;
}

#completion_prompt_manager .completion_prompt_manager_header,
.completion_prompt_manager_footer { padding: 0 !important; }

#completion_prompt_manager .completion_prompt_manager_header_advanced span {
  margin-left: 0;
  filter: none !important;
}

#completion_prompt_manager .completion_prompt_manager_header {
    color: var(--SmartThemeBodyColor) !important;
}

#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt span span span {
    filter: none !important;
}

#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
  background: var(--SmartThemeBlurTintColor);
  border-radius: 3px;
  border: 0.5px solid var(--SmartThemeBorderColor);
  box-shadow: 0.5px 0.5px 0 0.6px var(--混合), inset 0 0 6px var(--混合);
}

#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt_disabled {
    border: none !important;
    box-shadow: none !important;
    opacity: 0.35;
    background: none;
}

.completion_prompt_manager_prompt:not(.completion_prompt_manager_prompt_disabled) .completion_prompt_manager_prompt_name .prompt-manager-inspect-action,
.completion_prompt_manager_prompt:not(.completion_prompt_manager_prompt_disabled) .completion_prompt_manager_prompt_name .fa-solid,
.completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action,
.completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .fa-solid {
  color: var(--SmartThemeBodyColor) !important;
}

.world_entry { margin-top: 20px; background: transparent; }
.wi-card-entry { border-radius: 8px; border: none; outline: none; }

@media screen and (max-width: 1000px) {
  .world_entry_form .inline-drawer-header {
    border-radius: 8px;
    border: none;
    outline: none;
  }
}

#wiTopBlock .inline-drawer.wide100p .inline-drawer-content {
  background: color-mix(in srgb, var(--SmartThemeBlurTintColor) 60%, transparent) !important;
  margin-top: 5px;
  border-radius: var(--小圆角);
}

.select2-selection.select2-selection--multiple {
  background: color-mix(in srgb, var(--SmartThemeBorderColor) 25%, var(--SmartThemeBlurTintColor));
  border: none;
  border-radius: 5px;
}

.select2-container .select2-selection--multiple .select2-selection__choice,
.select2-container .select2-selection--single .select2-selection__choice {
  background: var(--混合);
}

.select2-selection__choice__remove,
.select2-selection__choice__display {
    background: transparent !important;
    line-height: 1.6;
}

.select2-search__field,
span.select2-selection.select2-selection--single {
    background: transparent !important;
    border-radius: 5px;
}

.select2-search__field,
.select2-dropdown.select2-dropdown--below { box-shadow: none !important; }

@media screen and (max-width: 768px) {
  div[name="ChatMessageHandlingToggles"] .checkbox-container.flex-container {
    flex-direction: column;
    align-items: flex-start;
    gap: 0;
    margin: 0 4px 0;
  }

  div[name="ChatMessageHandlingToggles"] .checkbox-container.flex-container input[type="checkbox"] {
    aspect-ratio: 1/1;
    width: 1em;
    height: auto;
  }
}

#bg_tabs,
#bg_chat_tab,
#bg_global_tab {
  border: none !important;
  box-shadow: none;
}

.bg_example {
    outline: none;
    border: none;
    box-shadow: 0 0 2px var(--SmartThemeBorderColor);
}

.BGSampleTitle { background: none !important; }

.mobile-only-menu-toggle {
    border-radius: 50% !important;
}

.mobile-only-menu-toggle,
.jg-menu {
    background: var(--SmartThemeBorderColor) !important;
    transform: scale(0.75) !important;
    opacity: 0.5;
}

.bg_example.selected-background {
    outline: 1.5px dashed var(--SmartThemeBorderColor);
}

#tavern_helper [data-folder-id="f8c1b0d6-829c-40a7-b352-38457ef75e9b"] {
  background: var(--混合);
}

@media (hover: none) and (pointer: coarse) {
  .regex-script-label { pointer-events: none; }
  .regex-script-label > *:not(.regex_script_name) { pointer-events: auto; }
  .regex-script-label .drag-handle {
    pointer-events: auto;
    padding: 6px 10px;
  }
}

.regex-script-label {
  border-radius: 3px !important;
  border: 0.8px solid var(--混合) !important;
  background: var(--内阴影);
}

.regex-script-label:has(.disable_regex:not(:checked)) {
    background: color-mix(in srgb, var(--SmartThemeBorderColor) 20%, #fff);
    box-shadow: 1px 1px 1px var(--混合), inset 0 0 15px var(--内阴影);
}

.regex_script_name { white-space: normal; }
.regex-script-label.flex-container { align-items: center; }
.regex-toggle-on { color: var(--标题栏-1); }

.example-extension_block {
  gap: 0 !important;
  margin: 0 !important;
}

.integrated-button-row {
  background: color-mix(in srgb, var(--SmartThemeBorderColor) 10%, var(--内阴影)) !important;
  border-radius: var(--小圆角) !important;
  padding: 1px 7px!important;
}

.integrated-button-row > *:not(.drag-handle):not(input[type="checkbox"]):not(label) {
  transform: scale(0.8);
}

#user_avatar_block .avatar-container.selected {
  background: var(--混合);
  border: none !important;
  border-radius: var(--小圆角);
  box-shadow: 2px 2px 4px var(--混合), inset 0 0 15px var(--内阴影);
}

#user_avatar_block .avatar-container.selected .ch_name {
  background: var(--内阴影);
  border-radius: var(--小圆角);
  color: var(--SmartThemeBorderColor);
  padding: 1px;
  box-shadow: inset 0 0 4px var(--混合);
}

.character_select { margin-top: calc(var(--mainFontSize) * 0.5); }

.character_select.is_fav {
  background: color-mix(in srgb, var(--SmartThemeBorderColor) 10%, #fff);
  border-radius: var(--小圆角);
  box-shadow: 0 0 4px var(--发光), 1px 1px 4px var(--混合), 0 0 2px var(--混合);
}

.character_select.is_fav .avatar { outline: none; }

.character_select.is_fav .ch_name {
  border-radius: 20px;
  max-width: 50%;
  padding: 0 10px;
  background: var(--混合);
  color: var(--SmartThemeQuoteColor);
  box-shadow: 0 0 4px var(--混合), inset 0 0 8px var(--内阴影);
}

.tag.actionable {
  min-height: calc(var(--mainFontSize) * 1.75);
  min-width: calc(var(--mainFontSize) * 1.75);
  margin: 0 auto 3px;
  border: 1px solid var(--混合) !important;
  filter: none;
  box-shadow: inset 0 0 2px var(--内阴影), 1px 1px 2px var(--混合), 0 0 2px var(--混合) !important;
}

#CharListButtonAndHotSwaps { margin-bottom: calc(var(--mainFontSize) * 0.05); }
#HotSwapWrapper { overflow: hidden; }
#HotSwapWrapper > div {
  overflow: auto hidden;
  flex-wrap: nowrap;
  max-height: calc(100vh + 20px);
}

#HotSwapWrapper .avatar {
  outline: 1px solid var(--混合);
  outline-offset: 2px;
  transform: scale(0.9);
}

.avatars_inline {
  gap: 3px;
  flex-wrap: nowrap;
  overflow: visible;
}

#show_more_messages {
  font-size: 0;
  background-color: var(--SmartThemeChatTintColor);
  padding: 2px 8px;
  outline: none;
  border-radius: var(--小圆角);
  box-shadow: 0 0 2.5px var(--混合), 1px 1px 2.5px var(--混合), inset 0 0 15px var(--内阴影);
  line-height: 1.6;
}

#show_more_messages::after {
  content: var(--加载更多消息);
  white-space: pre;
  color: var(--SmartThemeEmColor);
  font-style: italic;
  font-size: calc(var(--mainFontSize) * 0.75);
}

.swipeRightBlock:not(.last_mes .swipeRightBlock) {
   pointer-events: none;
}

/* 底栏输入框位置调整  */
#nonQRFormItems::before {
    left: calc(50% - 1px) !important;
}
