custom_css:
/* 此主题仅免费发布在Discord旅程社区，请勿二传或商用。感谢名单：图源来自xhs的@渔渔渔分享的壁纸，世界书界面设计来自柏柏老师的美化，思维链高度来自咩，正则适配来自Junezz老师。*/

/* 字体 */
@import url("https://fontsapi.zeoseven.com/415/main/result.css");

:root {
    --mainFontFamily: "WenYuan Serif SC VF";
    --monoFontFamily: "WenYuan Serif SC VF";
}

/* char图 */
.mes[is_user="false"] .mes_block::before {
    background-image: url('https://iili.io/fVsDFOQ.jpg');
}

/* user图 */
.mes[is_user="true"] .mes_block::before {
    background-image: url('https://iili.io/fVsDKbV.jpg');
}

:root {
    --图片高度: 250px;
    --图片宽度: 100%;
    --图片圆角: 0px;
    --渐变区域: 50px;
    --black30a: white;
}

#chat {
    border: none;
}

body.bubblechat .mes {
    padding: 0;
    background-color: transparent !important;
    border: none;
    flex-direction: column;
    margin: 20px 0;
}

.mes .avatar, .mes .name_text {
    display: none;
}

.mes_block {
    padding: 0 20px;
    padding-top: calc(var(--图片高度) - 15px) !important;
    position: relative;
    transform: none !important;
}

.mes_text {
    padding: 0;
    text-align: justify;
    margin-top: 60px;
}

/* 字距、行距、段距 */
.mes_text, .mes_text p {
    letter-spacing: 1px; /* 字距 */
    line-height: 1.5; /* 行距 */
}

.mes_text p {
    margin: 20px 0; /* 段距 */
    text-indent: 2em; /* 缩进 */
}

.mes_block::before {
    content: '';
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: var(--图片宽度);
    height: var(--图片高度);
    background-size: cover;
    background-position: center 45%;
    background-repeat: no-repeat;
    border-radius: var(--图片圆角);

    -webkit-mask-image: radial-gradient(
        ellipse 100% 70% at center,
        black 30%,
        transparent 70%
    );

    mask-image: radial-gradient(
        ellipse 100% 70% at center,
        black 30%,
        transparent 70%
    );

    -webkit-mask-composite: source-over;
    mask-composite: source-over;
    -webkit-mask-repeat: no-repeat;
    mask-repeat: no-repeat;
}

.mesAvatarWrapper {
    position: absolute;
    top: calc(var(--图片高度) + 10px);
    left: 0;
    width: 100%;
    height: 0;
    display: flex;
    justify-content: flex-end;
    padding-right: 20px;
    pointer-events: none;
    z-index: 10;
    gap: 10px; /* 元素之间的间距 */
}

body .mes .mes_timer, body .mes .mesIDDisplay, body .mes .tokenCounterDisplay {
    margin-right: 0 !important;
    opacity: 1;
    font-size: calc(var(--mainFontSize) * 0.9);
    font-weight: normal;
    color: var(--SmartThemeBodyColor);
}

body .mes .mesIDDisplay {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    top: 55px;
    white-space: nowrap;
    font-size: calc(var(--mainFontSize) * 1.1);
    font-weight: bold;
}

.mes .mes_timer:empty,
.mes .mesIDDisplay:empty,
.mes .tokenCounterDisplay:empty {
    display: none;
}

.mes .ch_name {
    justify-content: flex-start !important;
    flex-direction: column;
}

.mes .ch_name > .flex1 {
    flex: 0 1 auto !important;
    width: auto !important;
}

.mes .ch_name::before {
    display: block;
    color: var(--SmartThemeBodyColor);
    font-weight: bold;
    font-size: calc(var(--mainFontSize) * 1.5);
    letter-spacing: 2px;
}

/* char标题 */
.mes[is_user="false"] .ch_name::before {
    content: "灰色天空";
}

/* user标题 */
.mes[is_user="true"] .ch_name::before {
    content: "白色心情";
}

.mes .timestamp::before {
    content: "LOVE";
    display: inline-block;
    background-color: rgba(240, 240, 240, 1);
    color: rgba(255, 255, 255, 1);
    padding: 3px 8px;
    margin-right: 5px;
    font-size: calc(var(--mainFontSize) * 1.1) !important;
    line-height: 1;
    font-weight: bold;
    border-radius: 8px;
    box-shadow:
    inset 0px 2px 3px 1px rgba(235, 235, 235, 1),
    inset 0px 10px 10px 1px rgba(205, 205, 205, 1),
    0px 0px 10px 0px white;
    text-shadow: 0 3px 3px white;
}

.mes .timestamp {
    font-size: calc(var(--mainFontSize) * 0.9);
    font-weight: normal;
    opacity: 1;
    color: var(--SmartThemeBodyColor);
}

.mes_buttons {
    font-size: calc(var(--mainFontSize) * 0.9) !important;
    color: var(--SmartThemeBodyColor) !important;
    position: absolute;
    top: calc(var(--图片高度) - 10px);
    right: 15px;
    z-index: 15;
    overflow: visible;
}

.mes_button, .extraMesButtons>div {
    filter: none !important;
    opacity: 1 !important;
}

.extraMesButtons>div {
    font-size: 120% !important;
}

.mes .fa-ellipsis:before {
    content: "(幸福";
    font-family: var(--mainFontFamily);
    font-weight: normal !important;
}

.mes_buttons .fa-pencil:before {
    content: "像雨落下)";
    font-family: var(--mainFontFamily);
    font-weight: normal !important;
}

.mes_edit_buttons {
    position: absolute;
    transform: translateY(100%);
    right: 15px;
    filter: none !important;
}

.mes_edit_buttons .menu_button, .mes_reasoning_actions .edit_button {
    opacity: 1 !important;
}

.mes_text q {
    background-color: rgba(240, 240, 240, 1);
    border-radius: 10px;
    box-shadow:
    inset 0px 2px 2px 1px rgba(235, 235, 235, 1),
    inset 0px 5px 5px 1px white,
    0px 0px 3px 0px white;
    padding: 0px 5px;
    line-height: 1.8;
}

.mes_text em, code:not(pre code.hljs) {
    background: transparent !important;
    border: none !important;
    font-style: italic;
    color: var(--SmartThemeEmColor);
    border-radius: 20px !important;
    box-shadow:
    inset 0px -5px 10px 1px white,
    0px 0px 10px 0px white;
    padding: 0px 10px;
    line-height: 1.8;
}

.mes_text pre code, .mes_text blockquote {
    color: var(--SmartThemeBodyColor);
    background: transparent !important;
    border: none;
    border-radius: 0;
    border-left: 3px solid rgba(210, 210, 210, 1);
    padding: 0 10px;
    margin: 10px 0;
    overflow: visible;
    line-height: 1.8;
}

/* 思维链 */
.mes_reasoning_details, .mes_reasoning_details .mes_reasoning_summary {
    margin-right: 0 !important;
}

.mes_reasoning_header {
    background: transparent !important;
    margin: 0 !important;
    margin-bottom: 10px !important;
    padding: 0 !important;
    top: 44px;
    left: 50%;
    transform: translateX(-50%);
    pointer-events: auto;
}

.mes_reasoning_header_title {
    font-size: 0 !important;
}

.mes_reasoning_header_title::after {
    content: "『 伞下有你 』";
    font-size: var(--mainFontSize);
    white-space: nowrap;
    letter-spacing: 3px;
}

.mes_reasoning_arrow {
    display: none;
}

.mes_reasoning_actions {
    place-items: center;
    position: absolute;
    right: 20px;
}

.mes_reasoning {
    color: var(--SmartThemeBodyColor);
    margin-top: 50px;
    padding: 20px 15px;
    border: none;
    border-top: 1px solid var(--SmartThemeBodyColor);
    border-bottom: 1px solid var(--SmartThemeBodyColor);
}

.reasoning_edit_textarea {
    margin-top: 70px;
}

/* 导航栏与底栏 */
#send_form, body.no-blur #send_form, #top-bar, body.no-blur #top-bar {
    outline: none !important;
    border: none !important;
    background-color: var(--SmartThemeChatTintColor) !important;
    box-shadow: none;
    border-radius: 0;
}

#top-settings-holder .drawer-icon:before {
    font-size: 20px !important;
    font-weight: 500 !important;
    content: "⸝⸝⸝";
}

#top-settings-holder .drawer-icon.openIcon:before {
    content: "☂︎" !important;
    font-size: 20px !important;
}

#rightSendForm>div, #leftSendForm>div, #rightSendForm > div > [class*="fa-"], #rightSendForm > div > i, #quickPersona {
    font-size: 18px !important;
}

#rightSendForm>div:not(.mes_stop), #leftSendForm>div {
    opacity: 1;
}

#send_form .mes_stop {
    padding: 0 7px 7px;
    opacity: 1;
}

#nonQRFormItems {
    margin: 5px 15px;
    /* 减去左右 margin 的总和 (20px * 2 = 40px) */
    width: calc(100% - 40px) !important;
}

#send_textarea, #send_textarea:focus {
    clip-path: none !important;
    border: none !important;
    backdrop-filter: none !important;
    background-color: transparent !important;
}

#send_textarea::placeholder {
    color: transparent !important;
    text-shadow: none !important;
}

#form_sheld {
    margin: 0 auto !important;
}

#sheld {
    top: var(--bottomFormBlockSize);
    height: calc(100dvh - var(--bottomFormBlockSize));
    max-height: calc(100dvh - var(--bottomFormBlockSize));
    min-height: calc(100dvh - var(--bottomFormBlockSize));
}

/* 滚动条 */
::-webkit-scrollbar {
    width: 5px;
    background-color: transparent;
}

::-webkit-scrollbar-thumb {
    border: none !important;
    box-shadow: none !important;
    background-color: rgba(230, 230, 230, 1) !important;
    border-radius: 0 !important;
}

#HotSwapWrapper::-webkit-scrollbar, #tableContainer>div::-webkit-scrollbar, .history-tabs::-webkit-scrollbar {
    height: 5px;
}

@media screen and (max-width: 700px) {
  #chat::-webkit-scrollbar, .drawer-content::-webkit-scrollbar, #rm_print_characters_block::-webkit-scrollbar, .fillLeft .scrollableInner::-webkit-scrollbar {
    display: none;
  }
}

/* 切换消息 */
#chat .mes:last-of-type .mes_block {
    margin-bottom: 70px !important;
}

.swipe_right, .swipe_left {
    opacity: 0.5 !important;
    font-size: 0.9em !important;
}

.swipe_left {
    bottom: 25px;
}

.swipeRightBlock {
    width: 100%;
    display: flex;
    justify-content: center;
}

.swipes-counter {
    opacity: 1 !important;
    font-size: 0.9em !important;
    letter-spacing: 2px;
    font-weight: normal;
    position: absolute;
    bottom: 30px;
}

.swipes-counter::before {
    content: '— ';
    margin-right: 7px;
}

.swipes-counter::after {
    content: ' —';
    margin-left: 7px;
}

.swipe_right {
    position: absolute;
    bottom: 30px;
    right: 20px;
}

/* 菜单 */
#left-nav-panel, #completion_prompt_manager_popup, #rm_api_block, #AdvancedFormatting, #WorldInfo, #user-settings-block, #Backgrounds, #rm_extensions_block, .drawer-content.openDrawer, #right-nav-panel,
body.no-blur .popup, body.no-blur #bulk_tag_popup, body.no-blur #dialogue_popup,
#options, #extensionsMenu, .popup .popper-modal,
body.no-blur * .select2-dropdown {
  border: none !important;
  padding: 10px;
  border-radius: 0px;
  box-shadow: none;
  background-image: linear-gradient(rgba(255, 255, 255, 0.5), rgba(255, 255, 255, 0.5)), url('https://iili.io/fVsD35x.jpg');
  background-size: cover;
  background-position: center center;
  background-repeat: no-repeat;
}

/* 图标样式 */
.fa, .fa-brands, .fa-classic, .fa-regular:not(.fa-star), .fa-sharp, .fa-solid:not(.fav_on, .world_set, .fa-power-off, .fa-chevron-right, .fa-chevron-left, .mes .fa-solid), .fab, .far, .fas, #amily2_drawer_icon.closedIcon {
  color: rgba(200, 200, 200, 1) !important;
  font-size: 15px !important;
  filter: none;
  opacity: 1 !important;
}

/* 图标按钮 */
.menu_button {
    color: var(--SmartThemeBodyColor);
    filter: none;
    background-color: rgba(244, 244, 244, 1) !important;
    border-radius: 5px !important;
    border: none !important;
    box-shadow:
    inset 3px 3px 5px 0px white,
    3px 3px 5px 0px rgba(245, 245, 245, 1);
}

/* 输入框 */
input:not(input[type="range"], input[type='checkbox']), select, .text_pole, .textarea_compact, textarea:not(.select2-search__field),
#qr--settings #qr--set-qrList .qr--set-qrListContents > .qr--set-item .qr--content .qr--set-itemMessage,
#extensionSideBar #extensionSideBarContainer .sideBarItem.selected,
.select_chat_block[highlight] {
    background-color: rgba(240, 240, 240, 1) !important;
    border-radius: 20px !important;
    border: 2px solid white !important;
    box-shadow:
    inset 0px -2px 3px 1px rgba(235, 235, 235, 1),
    inset 0px -10px 10px 1px white,
    0px 0px 10px 0px white;

    padding: 3px 10px;
    margin-bottom: 5px !important;
}

select:focus-visible, input:focus-visible, textarea:not(#send_textarea):focus-visible {
    outline: none !mportant;
    background-color: rgba(255, 255, 255, 0.5) !important;
}

/* 标题 */
strong::before, b::before, h4:not(.margin0) span::before, #persona-management-button.drawer .standoutHeader::before {
  content: "ฅ";
  margin-right: 5px;
  font-size: 20px;
  font-weight: 100;
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader, .inline-drawer-header {
  border: none;
  background-image: none !important;
  font-size: 1em;
}

/* Spacing */
#anchor_checkbox label, #power-user-option-checkboxes label, .checkbox_label, .auto_continue_settings_block {
  margin: 7px 0px;
}

/* 勾选框 */
#anchor_checkbox label, #power-user-option-checkboxes label, .checkbox_label, .auto_continue_settings_block {
    place-items: flex-start;
}

input[type="checkbox"] {
    background-color: transparent;
    border: none;
    box-shadow: none;
    outline: none;
    filter: none;
    width: 1em;
    height: 1.5em;
    cursor: pointer;
    display: inline-flex;
    justify-content: center;
    align-items: center;
    vertical-align: middle;
    margin: 0 5px;
    position: relative;
}

input[type="checkbox"]::after {
    content: "❀";
    font-size: 1em;
    line-height: 1;
}

input[type="checkbox"]:checked::after {
    content: "✿";
    color: var(--SmartThemeShadowColor);
}

input[type="checkbox"]:hover::after {
    color: black;
}

input[type="checkbox"]::before {
    display: none;
}

/* 选择栏勾选框 */
.select2-results__option--selected.select2-results__option::before {
    content: "✿";
    line-height: normal !important;
}

.select2-results__option::before {
    content: "❀";
    border: none !important;
    margin-top: -10px !important;
}

/* 滑动条 */
input[type="range"], input[type="range"]:disabled {
  filter: none;
  height: 10px !important;
  background-color: rgba(245, 245, 245, 1) !important;
  border-radius: 20px !important;
  border: 1px solid white !important;
  box-shadow:
    inset 0px -2px 3px 1px rgba(235, 235, 235, 1),
    inset 0px -10px 10px 1px white,
    0px 0px 10px 0px white !important;
}

input[type="range"]::-webkit-slider-thumb {
  cursor: pointer;
  width: 30px;
  height: 10px;
  background-color: white !important;
  border-radius: 20px !important;
  border: 2px solid white !important;
  box-shadow:
    inset 0px 1px 3px 0px rgba(200, 200, 200, 1),
    inset 0px -5px 10px 1px white;
  margin-bottom: 5px;
}

/* 预设小字 */
.toggle-description {
  color: var(--SmartThemeBodyColor);
}

/* 预设提示词标题 */
#completion_prompt_manager .completion_prompt_manager_header_advanced span, #completion_prompt_manager .completion_prompt_manager_header div {
  filter: none;
  color: var(--SmartThemeBodyColor);
}

/* 预设条目 */
#completion_prompt_manager #completion_prompt_manager_list {
    gap: 5px;
    background-color: transparent !important;
    border-radius: 20px !important;
    border: 1px solid rgba(235, 235, 235, 1) !important;
    box-shadow:
    inset 0px -2px 3px 1px rgba(235, 235, 235, 1),
    inset 0px -10px 10px 1px white,
    0px -3px 5px 1px white;
}

/* 未启用预设条目 */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled,
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
  border: none;
  color: var(--SmartThemeBodyColor);
}

/* 编辑按钮 */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt .prompt_manager_prompt_controls span {
  filter: none !important;
  opacity: 1 !important;
}

/* 启用条目 */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt:not(#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled) {
    background-color: rgba(245, 245, 245, 1) !important;
    border-radius: 10px !important;
    border: 0px solid white !important;
    box-shadow:
    inset 0px -2px 3px 1px rgba(235, 235, 235, 1),
    inset 0px -10px 10px 1px white,
    -3px -3px 5px 0px white;
}

/* 世界书 */
.select2_multi_sameline+span.select2-container .select2-selection--multiple, span.select2.select2-container .select2-selection__choice__remove, .select2_choice_clickable_buttonstyle+span.select2-container .select2-selection__choice__display, .select2_multi_sameline+span.select2-container .select2-selection--multiple .select2-selection__choice, .standoutHeader~.inline-drawer-content {
  border: none;
  background-color: transparent;
}

.select2_multi_sameline+span.select2-container .select2-selection--multiple .select2-selection__choice {
  border: 1px solid black !important;
  border-radius: 0;
}

.wi-card-entry {
  margin: 10px 0;
  padding: 10px;
}

.disabledWIEntry {
  filter: none !important;
  opacity: 1 !important;
}

.select2-results .select2-results__option--selectable {
  opacity: 1 !important;
}

.select2-container .select2-selection--multiple, .select2-container .select2-selection--single, .select2-container .select2-selection--multiple .select2-selection__choice, .select2-container .select2-selection--single .select2-selection__choice {
    border: none !important;
}

.wi-card-entry {
    border: none !important;
    border-radius: 0;
}

.wi-card-entry:not(.disabledWIEntry .wi-card-entry) {
    border-radius: 20px !important;
    border: 1px solid rgba(235, 235, 235, 1) !important;
    box-shadow:
    inset 0px -2px 3px 1px rgba(235, 235, 235, 1),
    inset 0px -10px 10px 1px white,
    0px -3px 5px 1px white;
}

/* 世界书布局参考柏柏美化，感谢 */
@media screen and (max-width: 700px) {

    .wi-card-entry .inline-drawer-header:not(.world_entry .inline-drawer-header-pointer) {
        display: grid !important;
        grid-template-columns: auto auto auto auto auto auto 30px !important;
        grid-template-rows: auto auto auto !important;
    }

    .wi-card-entry .world_entry_thin_controls,
    .wi-card-entry .world_entry_thin_controls > .alignitemscenter,
    .wi-card-entry .WIEntryTitleAndStatus,
    .wi-card-entry .WIEnteryHeaderControls {
        display: contents !important;
    }

    .wi-card-entry .WIEntryTitleAndStatus > .flex-container {
        grid-row: 1;
        grid-column: 1 / 6;
    }

    .wi-card-entry select[name="entryStateSelector"] {
        grid-row: 1;
        grid-column: 6;
    }

    .wi-card-entry .drag-handle {
        grid-row: 1;
        grid-column: 7;
        justify-self: center !important;
        font-size: 17px !important;
    }

    .wi-card-entry div[name="PositionBlock"] {
        grid-row: 2;
        grid-column: 1 / 6;
    }

    .wi-card-entry .WIEnteryHeaderControls > div:has(input[name="depth"]) {
        grid-row: 2;
        grid-column: 6;
    }

    .wi-card-entry .killSwitch {
        grid-row: 2;
        grid-column: 7;
        justify-self: center !important;
        font-size: 17px !important;
    }

    .wi-card-entry .WIEnteryHeaderControls > div:has(input[name="order"]) {
        grid-row: 3;
        grid-column: 1;
    }

    .wi-card-entry .WIEnteryHeaderControls > div:has(input[name="probability"]) {
        grid-row: 3;
        grid-column: 2;
        justify-self: center;
    }

    .wi-card-entry .move_entry_button {
        grid-row: 3;
        grid-column: 3;
        font-size: 16px !important;
        align-self: center !important;
    }

    .wi-card-entry .duplicate_entry_button {
        grid-row: 3;
        grid-column: 4;
        font-size: 16px !important;
        align-self: center !important;
    }

    .wi-card-entry .delete_entry_button {
        grid-row: 3;
        grid-column: 5;
        font-size: 16px !important;
        align-self: center !important;
    }

    .wi-card-entry .inline-drawer-toggle:not(.world_entry .inline-drawer-header-pointer) {
        grid-row: 3;
        grid-column: 7;
        justify-self: center !important;
        font-size: 17px !important;
    }

    .wi-card-entry .wide100p {
        margin: 5px 2px;
    }
}

/* 图片 */
.bg_example {
  border: none;
  box-shadow: none;
  outline: none;
}

.extensions_info .extension_block {
  border: none;
}

/* QR */
.popup:has(#qr--modalEditor) .popup-content > #qr--modalEditor > #qr--main > .qr--modal-messageContainer > #qr--modal-messageHolder > #qr--modal-message {
  color: var(--SmartThemeBodyColor) !important;
}

/* 正则 */
.regex-script-label:not(:has(input[type="checkbox"]:checked)) {
    background-color: rgba(245, 245, 245, 1) !important;
    border-radius: 20px !important;
    border: 2px solid white !important;
    box-shadow:
    inset 0px -2px 3px 1px rgba(235, 235, 235, 1),
    inset 0px -10px 10px 1px white,
    0px 0px 10px 0px white;
}

