/* ===== 仅弹窗自身生效 ===== */
:root {
  --popup-bg: rgba(255,255,255,.95);
  --popup-border: #f0f0f0;
  --popup-shadow: 0 8px 32px rgba(0,0,0,.12);
  --popup-blur: 20px;
}
.popup-overlay,
.popup-backdrop,
div[class*="popup"]:not(.popup-content):not(.popup-body),
div[role="alert"]:not(.alert):not(.toast),
div[class*="message-box"]:not(.message-box-content) {
  backdrop-filter: blur(var(--popup-blur)) !important;
  -webkit-backdrop-filter: blur(var(--popup-blur)) !important;
  background: rgba(0,0,0.25) !important;
}
.popup.popup--animation-fast,
.alert,
.toast,
.popover,
.modal,
.notification,
div[role="alert"],
div[class*="message-box"] {
  background: var(--popup-bg) !important;
  border-radius: 16px !important;
  box-shadow: var(--popup-shadow) !important;
  border: 1px solid var(--popup-border) !important;
  padding: 16px 20px !important;
  color: #1f2937 !important;
  font-family: -apple-system, BlinkMacSystemFont, "SF Pro Text", sans-serif !important;
  max-width: 320px !important;
  width: fit-content !important;
  box-sizing: border-box !important;
}
.popup *,
.alert *,
.toast *,
.popover *,
.modal *,
.notification * {
  font-size: 12px !important;
  line-height: 1.5 !important;
}


/* ===== QR 区折叠与美化 ===== */
/* 1. 默认折叠，不占高度 */
#qr--bar {
  max-height: 0 !important;
  overflow: hidden !important;
  transition: max-height .3s ease !important;
  opacity: 0 !important;
  margin: 0 !important;
  padding: 0 !important;
}

/* 2. 展开（hover 演示，可替换为 .qr-bar-open） */
#send_form:hover #qr--bar,
#qr--bar.qr-bar-open {
  max-height: 200px !important;
  opacity: 1 !important;
  padding: 10px 0 !important;
  overflow-y: auto !important;
  max-width: calc(100% - 20px) !important;
  margin: 0 auto !important;
}

/* 3. 按钮统一间距与样式 */
.qr--buttons {
  margin: 5px 8px !important;
}
.qr--buttons > div {
  padding: 8px 16px !important;
  border-radius: 12px !important;
  background-color: rgba(240, 240, 245, .8) !important;
  border: 1px solid #eee !important;
  color: #333 !important;
  font-size: 12px !important;
  white-space: nowrap !important;
  cursor: pointer !important;
  transition: all .2s ease !important;
}
.qr--buttons > div:hover {
  background-color: rgba(220, 220, 230, .9) !important;
  transform: translateY(-2px) !important;
  box-shadow: 0 2px 6px rgba(0, 0, 0, .08) !important;
