  color: #BABABA !important;
  margin-left: -380px !important;
  padding: 0 8px !important;
}
#chat .mes:last-child {
  position: relative !important; }
#chat .mes:last-child:has(.swipes-counter):hover .swipe_left,
#chat .mes:last-child:has(.swipes-counter):hover .swipe_right {
  opacity: 1 !important;
  transform: translateY(0) !important;   pointer-events: auto !important;
}
#chat .mes:last-child:has(.swipes-counter):hover .swipes-counter {
  opacity: 1 !important;
  transform: translateY(0) !important;   pointer-events: auto !important;
}
.mes:not(:last-child) .swipe_left,
.mes:not(:last-child) .swipe_right,
.mes:not(:last-child) .swipes-counter,
.mes:not(:has(.swipes-counter)) .swipe_left,
.mes:not(:has(.swipes-counter)) .swipe_right,
.mes:not(:has(.swipes-counter)) .swipes-counter {
  opacity: 0 !important;
  pointer-events: none !important;
}
.mes[is_user="true"] .swipe_left,
.mes[is_user="true"] .swipe_right,
.mes[is_user="true"] .swipes-counter {
  opacity: 0 !important;
  pointer-events: none !important;
  display: none !important; /* 彻底隐藏，避免占位 */
}
