/*é¡¶æ */
#top-bar {
    position: fixed;
    pointer-events: none;
    z-index: 99;
    background: none;
    border: 0;
    background-color: rgba(0, 0, 0, 0) !important;
    box-shadow: none;
    overflow: visible;
    width: auto;
    height: auto;
    left: 0;
    right: auto;
}

#top-bar::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: auto;
    bottom: auto;
    width: 100%;
    height: 100%;
    background-image: url('https://iili.io/feh6yS2.png');
    background-size: auto;
    background-repeat: no-repeat;
    background-position: left top;
    border: 0px solid rgba(255, 255, 255, 0);
    border-radius: 0px;
    z-index: 999;
    filter: 
    contrast(1)      /* ğŸ–Œå¯¹æ¯”åº¦ */
    brightness(1)    /* ğŸ–Œæš—åº¦*/
    saturate(1)      /* ğŸ–Œé¥±å’Œåº¦ */
    hue-rotate(1deg)  /* ğŸ–Œè‰²ç›¸ */
}

@media screen and (max-width: 9999px) {
    #top-bar {
        width: 100vw;
        height: auto;
        overflow: visible !important;
    }

    #top-bar::before {
        min-width: 100%;
        min-height: 100px;
        background-size: contain;
        background-position: center top;
    }
}


