/* == 全局设置 & 字体== */
:root {
    --topBarBlockSize: 27px; 
    --box-border-radius: 5px;
    --top-distance: 7px;
    --scroll-width: 4px;

/*水平方向都是越小越左，垂直方向都是越小越上*/

/* == 调整名字和时间戳位置== */
--名字水平方向:140px; 
--名字垂直方向:-12px;  
--时间戳水平方向:0px;
--时间戳垂直方向:-12px ;

/* == 调整消息元三要素位置== */
/*楼层*/
--楼层水平方向:210px;
--楼层垂直方向:-65px;  

/*消耗*/
--token水平方向:210px; 
--token垂直方向:60px;  

/*时间*/
--时间水平方向:-100px; 
--时间垂直方向:-30px;  

/* == 操作按钮位置== */
--操作按钮水平方向:-9px;
--操作按钮垂直方向:10px;
}

body,
.text_pole:not(#completion_prompt_manager_list),
select,
textarea:not(#send_textarea),
input {
    font-family: "ChillHuoKai";
    font-weight: normal;

}

code,
code * {
    font-family: "ChillHuoKai" !important;
    font-weight: normal !important;
}

/* == 聊天主界面布局 == */
/* 二改自kakaa老师的双色盒子 */
#sheld {
    height: calc(100dvh - var(--topBarBlockSize));
    background-size: cover !important;
}
/*其他背景图*/
#left-nav-panel.drawer-content,
#WorldInfo.drawer-content,
#user-settings-block.drawer-content,
#right-nav-panel.drawer-content,
#rm_extensions_block.drawer-content,
#PersonaManagement.drawer-content{
    background-repeat: no-repeat!important;
    background-size: cover!important;   
}
#left-nav-panel.drawer-content{background-image: url('https://iili.io/q32Ijbn.jpg');}
#WorldInfo.drawer-content{background-image: url('https://iili.io/q32u93l.png');}
#user-settings-block.drawer-content{background-image: url('https://iili.io/q2lIgbn.jpg');}
#rm_extensions_block.drawer-content{background-image: url('https://iili.io/q32u93l.png');}
#PersonaManagement.drawer-content{background-image: url('https://iili.io/q2lIgbn.jpg');}
#right-nav-panel.drawer-content{background-image: url('https://iili.io/q32u93l.png');}


#chat {
    background: transparent !important;
    background-color: transparent !important;
    backdrop-filter: none;
    border: none !important;
    overflow-y: scroll;
    align-items: center;
    max-height: calc(100dvh - var(--topBarBlockSize) - 20px) !important;
    margin-left:3px;
    border-radius: 0 0 var(--box-border-radius) var(--box-border-radius);
}

@supports (-webkit-touch-callout: none) {
    #chat {
        padding-right: var(--scroll-width);
    }
}

/*滚动条贴图学习了千尾老师*/
::-webkit-scrollbar {
    width: 4px;
}

::-webkit-scrollbar-track {
    background: transparent!important;
    padding: 0;
   border: none;
}
::-webkit-scrollbar-thumb{

  background-color: #ffa95e !important;
  background-repeat: no-repeat;
  background-position: center center;
  border: none !important;
  box-shadow: none !important;
  border-radius: 20px;}
::-webkit-scrollbar-thumb:vertical,
::-webkit-scrollbar-thumb:horizontal {
    background-color: #ffffff;
    box-shadow: none;
    border: 0;
}

/* == 消息气泡 == */
.mes {
    width: 100%;
  position: relative;
    min-height:auto; /* 增加最小高度 */
    padding: 8px 10px;
    display: flex;
    flex-direction: column;
    align-items: center;   
    margin:  8.5rem auto ;     
    border-radius: 0px!important;
    background: none;
    background-size: 100%;
    background-image:url('https://iili.io/q2loJS9.jpg');
     background-repeat: repeat-y;
    background-position: top center;
}
.last_mes {
  margin-bottom: 5.5rem !important;
}
.mes::before {
  content: "";
  position: absolute;
  top: -20px; 
  left: 50%; 
  transform: translateX(-50%);
  width: 100% ;      
  height: 100% ; 
  background: url('https://iili.io/q2QHZaj.png') top center / contain no-repeat;
}




.mes::after {
    content: "";
    position: absolute;
    bottom: 0px; 
  left: 50%; 
  transform: translateX(-50%);
  width: 100%; 
  height: 220px;

    background-image: url('https://iili.io/q2rfGm7.png');
    background-size: cover;
    background-repeat: no-repeat;
    opacity: 0.7 !important;
    pointer-events: none;
    z-index:99;
}

/* ==头像与名字 == */
.mesAvatarWrapper {
    position: relative;    
    top:50px;
    right:22%;
    transform: none !important;
    padding: 3px !important;    
    margin-bottom: 150px;
 }

 #chat .mes .avatar {

    position: absolute;
    display: inline-block;
    padding: 8px;
    top:-44px;
    left:-60px;
    opacity: 1!important;
    transform:scale(2.2);

}
#chat .mes .avatar img {
    border-radius: 50%;
    position:absolute;
    width:72px;
    height:72px;
    object-fit: cover;
    object-position: center 30%;
    opacity:0.9;
    z-index:1;    
   box-shadow:none;
}
#chat .mes .avatar::before {
    content: "";
    position: absolute;
    top: 2px;
    left: 2px;
    width: calc(160%);
    height: calc(160%);
    background-image: url("https://iili.io/qF2nLpR.png");
    background-size: contain;
    background-repeat: no-repeat;
    z-index: 100;
    pointer-events: none;
    opacity:1;
}

#quickPersona{
position:absolute;
left:0px;
opacity:1!important;
box-shadow:none!important;
}




/* 头像选中框 */
.avatar-container.interactable.selected {
    background: url('https://iili.io/qFE1lxn.png') no-repeat center center !important;
    background-size: cover !important; 
}




/* ==消息内容与排版== */

.mes_block {
    width: 100%;
    padding-left: 0;
    padding-right: 0;
    z-index:999;
    position:relative;
    padding-bottom: 35px;
    padding-top: 25px;
    overflow:visible;
}
/* 名字栏 */
.ch_name.flex-container.justifySpaceBetween{
    position: relative;
   margin-bottom:10px;
   z-index:999;

}
.mes .name_text {
     font-size: 15px;
     font-weight: 600;
     color: #c9c8b4;
     position: absolute;
     left:var(--名字水平方向);
    top:var(--名字垂直方向);
     display: block;
     transform-origin: center;
     text-align: left; 
     z-index: 999;
     transform: none!important;
     opacity: 1!important;
     height: auto;  
     white-space: normal;
     word-break: break-all;
     box-sizing: border-box; 
     margin-left: 10px; 
     width: auto; 
}


.mes .timestamp {
    font-size: 15px;
    font-weight: 600;
    color: #c9c8b4;
    position: absolute;
    left:var(--时间戳水平方向);
    top:var(--时间戳垂直方向) ;
    display: flex;
    transform-origin: center;
    transform:none!important;
    text-align: center;
   opacity:1!important;
    z-index:99;
}
.mes .timestamp::before {

  content: "";
    position: absolute;
    top: -90px;
    left:-25px;
    width: calc(800% + 100px);
    height: calc(800%);
    background-image: url("");
    background-size: contain;
    background-repeat: no-repeat;
    z-index: 9999;
    pointer-events: none;
    opacity:1;
}
.mes .timestamp::after { 
   content: "";
   position: absolute;
   left: 75%; 
   transform: translateX(-50%);
   top: 100%; 
   margin-top: 2px; 
   white-space: nowrap; 
   z-index: 999; 
}

/*操作按钮*/
.mes_buttons{
position:absolute;
top: var(--操作按钮垂直方向); 
left: var(--操作按钮水平方向);
z-index:999;
width:100%;
gap:8px ;

}

/* 编辑按钮图标替换 */
.mes_button.mes_edit.fa-pencil.interactable {
    position: relative;  
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0; 
    color: transparent;
    border: none;
    background: transparent;
    cursor: pointer;
    z-index: 99;
    width: 20px;
    height: 20px;
    opacity:1!important;
}

.mes_button.mes_edit.fa-pencil.interactable::after {
    content: '' !important;
    position: absolute;     
    width: 100%;
    height: 100%;
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
    background-image: url('https://iili.io/q3KBWTg.png');
    font-size: 0;
    box-shadow: none !important;
}
/*编辑菜单位置调整  */
/*二改自易碎蝶老师*/
.mes_edit_buttons {
    position: absolute;
     top: 10px !important;   
    right:5px !important;   
    opacity: 0 !important;
    pointer-events: none !important;
    transition: opacity 0.2s !important;
   transform: scale(0.95);
}

.mes:hover .mes_edit_buttons,
.mes.selected .mes_edit_buttons {
    opacity: 1 !important;
    pointer-events: auto !important;
}
.mes_button, .extraMesButtons>div {
cursor: pointer;
    transition: opacity 0.2s ease-in-out;
    filter: drop-shadow(0px 0px 0px #ffffff) !important;   
opacity: 1 !important;

}
/* 消息操作按钮图标替换 */
.mes_button.extraMesButtonsHint.fa-ellipsis.interactable {
    position: relative;    
    width: 20px;
    height: 20px; 
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0; 
    color: transparent;
    border: none;
    background: transparent;
    cursor: pointer;
    z-index: 9999;
}
.mes_button.extraMesButtonsHint.fa-ellipsis.interactable::after {
    content: '' !important;
    position: absolute; 
    width: 100%;
    height: 100%;
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
    background-image: url('https://iili.io/q3KBWTg.png');
    font-size: 0;
    box-shadow: none !important;
}

.mes_button.favorite-toggle-icon.interactable {  
    color: #ffa95e !important;
    opacity:1!important; 

}

/* 源自12老师 */
.mes_text br:first-child{
    display: none !important;
}

.mes_text {
    width:100%;
    padding: 0px 0px 85px 4px !important;
    margin-top:25px;
    z-index: 999;
    flex: 1;
    font-size:15px;
}
.mes_text p {
    margin-bottom: 1em!important;
    line-height: 1.5;
}

q {
    background-color: rgba(235,93,31,0.7) !important; 
    padding: 2px 2px !important; 
    margin:4px 4px!important;
    border-radius: 5px !important;      
}


.mes_text code {
    display: inline-block;
    background-color: #FFF8F0 !important; /* 温柔奶白底 */
    padding: 8px 8px;
    margin: 5px 8px 5px 0 !important;
    color: #5A4535 !important; /* 浅棕文字，柔和耐看 */
    border: 0.9px solid #FFA85C !important; /* 浅橙细边框，柿柿如意主题 */
    border-radius: 4px !important;
    letter-spacing: 0.3px !important;
    word-spacing: 1px !important;
    line-height: 1.4 !important;
    font-size: 1em !important;
    box-shadow: 0 0 6px rgba(255, 168, 92, 0.15) !important;
}
.mes_text blockquote {   
    margin: 16px 8px 20px 0 !important;
    padding: 20px 24px !important;  
    position: relative;
    background-color: #FFF9F2 !important;
    border-left: 4px solid #FFA85C !important;
    color: #5A4535 !important;
    border-radius: 4px !important;
}


/*思维链 */
.mes_reasoning_header {
    position: relative !important;
    top: 8px !important;
    left: 8px !important;
     transform: none !important;
    transition: none !important;
    animation: none !important;
    width: 90% !important;
    height: 10px !important;
    color: transparent !important;
    font-size: 1em !important;
    background-color:rgba(225,225,225,0) !important;
    background-image: none !important;
    background-size: cover !important;
    background-position: center center !important;

}

.mes_reasoning_header.flex-container::before {
    content: "柿红桂花香";
    position: absolute !important;
    top: 50% !important;
    left: 50% !important;
    transform: translate(-50%, -50%) !important;
    color: #fff !important;
     background: radial-gradient(circle, 
     #FFBE77 0%, 
     #FFF2E4 50%, 
     #FFE6CA 100%
   );
    font-size: 1em !important;
    font-weight: 600 !important;
    letter-spacing: 1px !important;
    text-transform: uppercase !important;
    white-space: nowrap !important;
    z-index: 2 !important;
    padding:0px 25px;
    border-radius: 10%;
}
.mes_reasoning{
position:relative;
top:20px;}
/* ==消息元数据 == */
/* 定义统一偏移变量 */

.mesIDDisplay,
.tokenCounterDisplay,
.mes_timer {

    position: absolute !important;
    display: inline-block;
    transform-origin: left top;
    opacity: 1 !important;    
    font-size: 18px !important;
    font-weight: 500;
    text-align: right;
}


.mesIDDisplay { 
    left: calc(var(--楼层水平方向) + var(--meta-global-offset-x, 0px));
    top: calc(var(--楼层垂直方向) + var(--meta-global-offset-y, 0px));
    color: #c4b6a6;
    font-size: 19px !important;
}

.tokenCounterDisplay { 
    left: calc(var(--token水平方向) + var(--meta-global-offset-x, 0px));
    top: calc(var(--token垂直方向) + var(--meta-global-offset-y, 0px));
    color: #c4b6a6;
    font-size: 17px !important;
}

.mes_timer { 
    left: calc(var(--时间水平方向) + var(--meta-global-offset-x, 0px));
    top: calc(var(--时间垂直方向) + var(--meta-global-offset-y, 0px));
    color: #c4b6a6;
    font-size: 17px !important;
}

.mesIDDisplay:empty,
.tokenCounterDisplay:empty,
.mes_timer:empty {
    display: none !important;
}


.margin0 {
    font-size: 13px !important;
    font-weight: 600;
}


.swipe_left.fa-solid.fa-chevron-left.interactable::before,
.swipe_right.fa-solid.fa-chevron-right.interactable::before {
    content: "" !important; 
    font-family: initial !important; 
    font-size: 0 !important; 
    display: inline-block;   
    background-position: center;
    background-repeat: no-repeat;
    background-size: contain;    
    transform-origin: center;
    vertical-align: middle;
    overflow: visible; 
}


.swipe_right.fa-solid.fa-chevron-right.interactable {    
    transform: translateX(30px);
    opacity: 1 !important; 
}

.swipe_right.fa-solid.fa-chevron-right.interactable::before{
    position:absolute;
    width: 50px; 
    height: 50px;
    top:-20px;
    left:-50px;
}


.swipe_left.fa-solid.fa-chevron-right.interactable::before{
    position:absolute;
    width: 50px; 
    height: 50px;
    top:-20px;
    left:-50px;

}

.swipes-counter {
  position: absolute;
  font-size: 10px!important; 
  opacity: 1 !important;
  color: #000 !important;
  white-space: nowrap !important;
  z-index: 99;
  position: relative; 
  display: inline-flex; 
  align-items: center;  
  transform:scale(1.4);
   pointer-events: none;
}

.swipes-counter:empty {
     display: none;
 }



/* ==顶部栏== */
#top-bar {
    background-color: none!important;
    background-position: center;
    background-repeat: no-repeat;
    box-shadow: none;  
    position: relative;
    height: 30px;
    width: 100%;  
   background: transparent !important;
  background-color: transparent !important;
  backdrop-filter: none !important;
  -webkit-backdrop-filter: none !important;
  box-shadow: none !important;
  border: none !important; 
}
#top-bar::after {
content: "";
   position: absolute;  
   width: 100%;
   height: 82px; 
   background: url('https://iili.io/qdooWpS.png') top center repeat-x;
   background-size: auto 100%; 
   pointer-events: none;
   z-index: -1;
   opacity:!important;
}
#top-settings-holder{
position:relative;
top:-30px;}

